import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PublicCompanyChat = () => {
  const [company, setCompany] = useState(null);
  const [prompt, setPrompt] = useState('');
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loadingCompany, setLoadingCompany] = useState(true);

  useEffect(() => {
    fetchCompany();
  }, []);

  // üß† ÿ¨ŸÑÿ® ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ÿ±ŸÉÿ© ÿßŸÑÿ≠ÿßŸÑŸäÿ© (ÿßŸÑÿ™Ÿä ÿ£ŸÜÿ¥ÿ£ÿ™ ÿßŸÑŸÜŸÖŸàÿ∞ÿ¨)
  const fetchCompany = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/public/company');
      setCompany(res.data.company);
    } catch (err) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ÿ±ŸÉÿ©:', err);
    } finally {
      setLoadingCompany(false);
    }
  };

  // üí¨ ÿ•ÿ±ÿ≥ÿßŸÑ ÿ±ÿ≥ÿßŸÑÿ© ÿ•ŸÑŸâ ÿßŸÑÿ∞ŸÉÿßÿ° ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä ÿ®ÿßÿ≥ŸÖ ÿßŸÑÿ¥ÿ±ŸÉÿ©
  const handleSend = async (e) => {
    e.preventDefault();
    if (!prompt.trim() || !company?.apiKey) return;

    const userMsg = {
      role: 'user',
      content: prompt,
      timestamp: new Date().toISOString(),
    };
    setMessages((prev) => [...prev, userMsg]);
    setPrompt('');
    setLoading(true);

    try {
      const res = await axios.post('http://localhost:5000/api/public/chat', {
        companyApiKey: company.apiKey,
        prompt,
      });

      const aiMsg = {
        role: 'assistant',
        content: res.data.reply || `ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿ±ÿØ ŸÖŸÜ ${company.name}.`,
        timestamp: new Date().toISOString(),
      };
      setMessages((prev) => [...prev, aiMsg]);
    } catch (err) {
      const errMsg = {
        role: 'assistant',
        content: 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ™ŸàÿßÿµŸÑ ŸÖÿπ ÿßŸÑÿÆÿßÿØŸÖ. ÿ≠ÿßŸàŸÑ ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ ŸÑÿßÿ≠ŸÇŸãÿß.',
        timestamp: new Date().toISOString(),
        error: true,
      };
      setMessages((prev) => [...prev, errMsg]);
    } finally {
      setLoading(false);
    }
  };

  const formatTime = (timestamp) =>
    new Date(timestamp).toLocaleTimeString('ar-SA', {
      hour: '2-digit',
      minute: '2-digit',
    });

  // ‚è≥ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ÿ±ŸÉÿ©
  if (loadingCompany) {
    return (
      <div style={{ textAlign: 'center', padding: '60px', color: '#444' }}>
        ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ÿ±ŸÉÿ©...
      </div>
    );
  }

  // ‚ùå ŸÅŸä ÿ≠ÿßŸÑÿ© ÿπÿØŸÖ Ÿàÿ¨ŸàÿØ ÿ®ŸäÿßŸÜÿßÿ™ ÿ¥ÿ±ŸÉÿ©
  if (!company) {
    return (
      <div style={{ textAlign: 'center', padding: '50px', color: '#c00' }}>
        ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ÿ±ŸÉÿ©.
      </div>
    );
  }

  return (
    <div
      style={{
        maxWidth: '750px',
        margin: '40px auto',
        padding: '25px',
        borderRadius: '15px',
        background: '#ffffff',
        boxShadow: '0 5px 25px rgba(0,0,0,0.1)',
        fontFamily: 'Cairo, sans-serif',
      }}
    >
      <div
        style={{
          textAlign: 'center',
          marginBottom: '20px',
        }}
      >
        <h2 style={{ color: '#1976d2', marginBottom: '5px' }}>
          üí¨ ŸÖÿ≠ÿßÿØÿ´ÿ© ÿßŸÑÿ∞ŸÉÿßÿ° ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä ‚Äì {company.name}
        </h2>
        {company.description && (
          <p style={{ color: '#555', fontSize: '14px' }}>{company.description}</p>
        )}
      </div>

      <div
        style={{
          height: '420px',
          overflowY: 'auto',
          padding: '15px',
          background: '#f7f9fc',
          borderRadius: '12px',
          border: '1px solid #e3e6ec',
          marginBottom: '20px',
        }}
      >
        {messages.length === 0 ? (
          <p
            style={{
              textAlign: 'center',
              color: '#888',
              marginTop: '160px',
              fontSize: '15px',
            }}
          >
            ÿßÿ®ÿØÿ£ ÿßŸÑŸÖÿ≠ÿßÿØÿ´ÿ© ÿ®ÿ∑ÿ±ÿ≠ ÿ≥ÿ§ÿßŸÑŸÉ ÿπŸÑŸâ {company.name}
          </p>
        ) : (
          messages.map((msg, i) => (
            <div
              key={i}
              style={{
                display: 'flex',
                flexDirection: msg.role === 'user' ? 'row-reverse' : 'row',
                alignItems: 'flex-start',
                marginBottom: '12px',
              }}
            >
              <div
                style={{
                  background:
                    msg.role === 'user' ? '#1976d2' : '#e9f2ff',
                  color: msg.role === 'user' ? '#fff' : '#222',
                  padding: '12px 16px',
                  borderRadius: msg.role === 'user'
                    ? '15px 15px 0 15px'
                    : '15px 15px 15px 0',
                  maxWidth: '75%',
                  boxShadow: '0 2px 6px rgba(0,0,0,0.08)',
                }}
              >
                <p style={{ margin: 0, fontSize: '15px' }}>{msg.content}</p>
                <small
                  style={{
                    opacity: 0.7,
                    fontSize: '12px',
                    display: 'block',
                    marginTop: '5px',
                    textAlign: msg.role === 'user' ? 'left' : 'right',
                  }}
                >
                  {formatTime(msg.timestamp)}
                </small>
              </div>
            </div>
          ))
        )}
        {loading && (
          <p
            style={{
              textAlign: 'center',
              color: '#555',
              fontStyle: 'italic',
              marginTop: '10px',
            }}
          >
            ÿ¨ÿßÿ±Ÿä ÿ™ŸàŸÑŸäÿØ ÿßŸÑÿ±ÿØ ŸÖŸÜ {company.name}...
          </p>
        )}
      </div>

      <form onSubmit={handleSend} style={{ display: 'flex', gap: '10px' }}>
        <input
          type="text"
          placeholder={`ÿßŸÉÿ™ÿ® ÿ±ÿ≥ÿßŸÑÿ™ŸÉ ÿ•ŸÑŸâ ${company.name}...`}
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          disabled={loading}
          style={{
            flex: 1,
            padding: '12px',
            borderRadius: '10px',
            border: '1px solid #ccc',
            outline: 'none',
            fontSize: '15px',
          }}
        />
        <button
          type="submit"
          disabled={loading || !prompt.trim()}
          style={{
            padding: '12px 25px',
            border: 'none',
            borderRadius: '10px',
            background: loading ? '#9ab9e3' : '#1976d2',
            color: 'white',
            fontWeight: 'bold',
            cursor: loading ? 'not-allowed' : 'pointer',
            transition: '0.3s',
          }}
        >
          {loading ? 'ÿ¨ÿßÿ±Ÿç ÿßŸÑÿ•ÿ±ÿ≥ÿßŸÑ...' : 'ÿ•ÿ±ÿ≥ÿßŸÑ'}
        </button>
      </form>
    </div>
  );
};

export default PublicCompanyChat;
