import React, { useState, useEffect } from "react";
import axios from "axios";

const ChatPage = () => {
  const [apiKey, setApiKey] = useState(localStorage.getItem("companyApiKey") || "");
  const [company, setCompany] = useState(null);
  const [prompt, setPrompt] = useState("");
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);

  // ✅ تحميل بيانات الشركة لما المستخدم يدخل API Key
  const fetchCompany = async () => {
    if (!apiKey.trim()) {
      alert("من فضلك أدخل مفتاح API الخاص بالشركة");
      return;
    }

    try {
      const res = await axios.get(`http://localhost:5000/api/public/company/${apiKey}`);
      if (res.data.success) {
        setCompany(res.data.company);
        localStorage.setItem("companyApiKey", apiKey);
      } else {
        alert("لم يتم العثور على بيانات الشركة");
      }
    } catch (err) {
      console.error("❌ Fetch company error:", err);
      alert("حدث خطأ أثناء تحميل بيانات الشركة");
    }
  };

  // ✅ إرسال الرسالة للذكاء الاصطناعي
  const handleSend = async (e) => {
    e.preventDefault();
    if (!prompt.trim()) return;
    if (!company) {
      alert("الرجاء تحميل بيانات الشركة أولاً!");
      return;
    }

    const userMsg = { role: "user", content: prompt };
    setMessages((prev) => [...prev, userMsg]);
    setPrompt("");
    setLoading(true);

    try {
      const res = await axios.post("http://localhost:5000/api/public/chat", {
        companyApiKey: apiKey,
        prompt,
      });

      const aiMsg = {
        role: "assistant",
        content: res.data.reply || "لم يتم الحصول على رد من الذكاء الاصطناعي.",
      };
      setMessages((prev) => [...prev, aiMsg]);
    } catch (err) {
      console.error("❌ Chat error:", err);
      const errMsg = {
        role: "assistant",
        content: "حدث خطأ أثناء التواصل مع الخادم.",
      };
      setMessages((prev) => [...prev, errMsg]);
    } finally {
      setLoading(false);
    }
  };

  const handleLogoutCompany = () => {
    localStorage.removeItem("companyApiKey");
    setCompany(null);
    setMessages([]);
    setApiKey("");
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-100 flex flex-col items-center py-8 px-4">
      <div className="w-full max-w-3xl bg-white shadow-2xl rounded-2xl p-6 border border-gray-200">
        <h1 className="text-2xl font-bold text-center text-blue-700 mb-6">
          🤖 AI Chat Assistant
        </h1>

        {/* ✅ إدخال الـ API Key */}
        {!company ? (
          <div className="flex flex-col items-center">
            <input
              type="text"
              placeholder="أدخل مفتاح API الخاص بشركتك..."
              value={apiKey}
              onChange={(e) => setApiKey(e.target.value)}
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 mb-4 text-right"
            />
            <button
              onClick={fetchCompany}
              className="bg-blue-600 text-white font-semibold py-2 px-6 rounded-lg hover:bg-blue-700 transition"
            >
              تحميل بيانات الشركة
            </button>
          </div>
        ) : (
          <>
            {/* ✅ عرض بيانات الشركة */}
            <div className="flex justify-between items-center bg-blue-50 p-4 rounded-lg mb-4">
              <div>
                <h2 className="font-bold text-lg text-blue-700">{company.name}</h2>
                <p className="text-gray-600 text-sm">
                  {company.description || "لا يوجد وصف للشركة"}
                </p>
              </div>
              <button
                onClick={handleLogoutCompany}
                className="text-sm text-red-500 hover:underline"
              >
                🔒 تسجيل خروج
              </button>
            </div>

            {/* ✅ صندوق المحادثة */}
            <div className="border border-gray-300 rounded-lg p-4 h-96 overflow-y-auto bg-gray-50 mb-4">
              {messages.length === 0 ? (
                <p className="text-center text-gray-400 mt-36">
                  ابدأ المحادثة الآن مع {company.name}
                </p>
              ) : (
                messages.map((msg, i) => (
                  <div
                    key={i}
                    className={`flex ${
                      msg.role === "user" ? "justify-end" : "justify-start"
                    } mb-2`}
                  >
                    <div
                      className={`px-4 py-2 rounded-2xl max-w-[75%] ${
                        msg.role === "user"
                          ? "bg-blue-600 text-white"
                          : "bg-gray-200 text-gray-800"
                      }`}
                    >
                      {msg.content}
                    </div>
                  </div>
                ))
              )}
              {loading && <p className="text-gray-400 text-center mt-2">⏳ جاري الرد...</p>}
            </div>

            {/* ✅ إدخال الرسالة */}
            <form onSubmit={handleSend} className="flex gap-3">
              <input
                type="text"
                placeholder="اكتب رسالتك هنا..."
                value={prompt}
                onChange={(e) => setPrompt(e.target.value)}
                className="flex-1 border border-gray-300 rounded-lg p-3 focus:outline-none focus:ring-2 focus:ring-blue-500 text-right"
              />
              <button
                type="submit"
                disabled={loading || !prompt.trim()}
                className="bg-green-500 text-white px-5 py-2 rounded-lg hover:bg-green-600 transition"
              >
                إرسال
              </button>
            </form>
          </>
        )}
      </div>
    </div>
  );
};

export default ChatInterface;
