import React, { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const ProjectChat = () => {
  const { projectId } = useParams();
  const { user } = useAuth();
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null);

  // تمرير focus للرسائل الجديدة
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  // جلب سجل المحادثة عند تحميل الصفحة
  useEffect(() => {
    async function fetchMessages() {
      try {
        const res = await fetch(`http://localhost:5000/api/chat/${projectId}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        });
        if (!res.ok) throw new Error(`Server responded with ${res.status}`);
        const data = await res.json();
        setMessages(data || []);
      } catch (err) {
        console.error('Failed to fetch messages:', err.message);
      }
    }
    fetchMessages();
  }, [projectId]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const msgText = input;
    setMessages((prev) => [...prev, { from: 'user', text: msgText, timestamp: new Date().toISOString() }]);
    setInput('');

    try {
      const res = await fetch(`http://localhost:5000/api/chat`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
        body: JSON.stringify({ message: msgText, projectId }),
      });

      if (!res.ok) throw new Error(`Server responded with ${res.status}`);
      const data = await res.json();

      setMessages((prev) => [...prev, { from: 'bot', text: data.message || 'No reply', timestamp: data.timestamp }]);
    } catch (err) {
      setMessages((prev) => [...prev, { from: 'bot', text: 'Error contacting API', timestamp: new Date().toISOString() }]);
      console.error(err.message);
    }
  };

  return (
    <div style={{ minHeight: '100vh', background: '#0b1117', display: 'flex', flexDirection: 'column', alignItems: 'center', padding: '40px', color: '#e6eef8' }}>
      <h1 style={{ marginBottom: '20px', color: '#ff77c6' }}>Project Chat</h1>
      <div style={{ width: '100%', maxWidth: '500px', background: 'rgba(15,20,28,0.8)', borderRadius: '18px', padding: '20px', display: 'flex', flexDirection: 'column', gap: '10px' }}>
        <div style={{ flex: 1, overflowY: 'auto', maxHeight: '400px' }}>
          {messages.map((msg, i) => (
            <div key={i} style={{ padding: '10px', borderRadius: '12px', background: msg.from === 'user' ? 'rgba(100,100,255,0.2)' : 'rgba(255,100,100,0.2)', marginBottom: '6px' }}>
              {msg.text}
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>
        <div style={{ display: 'flex', gap: '10px' }}>
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Type your message..."
            style={{ flex: 1, padding: '10px', borderRadius: '12px', border: 'none' }}
            onKeyPress={(e) => { if (e.key === 'Enter') sendMessage(); }}
          />
          <button onClick={sendMessage} style={{ padding: '10px 20px', borderRadius: '12px', border: 'none', background: '#667eea', color: '#fff', cursor: 'pointer' }}>Send</button>
        </div>
      </div>
    </div>
  );
};

export default ProjectChat;
