// CompanyChat.jsx
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { publicCompanyService } from "../services/api";

const CompanyChat = () => {
  const { companyId } = useParams();
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [company, setCompany] = useState(null);

  // جلب بيانات الشركة عند التحميل
  useEffect(() => {
    const fetchCompany = async () => {
      try {
        const res = await publicCompanyService.getCompany(companyId);
        if (res.success) setCompany(res.company);
        else console.error("Company not found");
      } catch (err) {
        console.error("Fetch company error:", err);
      }
    };
    fetchCompany();
  }, [companyId]);

  const sendMessage = async () => {
    const trimmed = input.trim();
    if (!trimmed) return;

    setMessages((prev) => [...prev, { sender: "user", text: trimmed }]);
    setInput("");

    try {
      // استدعاء API عبر publicCompanyService
      const res = await publicCompanyService.sendMessage(trimmed, companyId);
      const reply = res?.reply || "No reply from AI";
      setMessages((prev) => [...prev, { sender: "bot", text: reply }]);
    } catch (err) {
      console.error(err);
      setMessages((prev) => [...prev, { sender: "bot", text: "Error contacting AI" }]);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") sendMessage();
  };

  return (
    <div style={{ maxWidth: 600, margin: "0 auto" }}>
      <h2 style={{ textAlign: "center", marginBottom: 10 }}>
        {company ? `${company.name} AI Chat` : "Loading..."}
      </h2>

      <div
        id="chat-container"
        style={{ border: "1px solid #ccc", padding: 10, height: 400, overflowY: "auto", marginBottom: 10 }}
      >
        {messages.map((msg, idx) => (
          <div
            key={idx}
            style={{
              marginBottom: 5,
              color: msg.sender === "user" ? "blue" : "green",
              textAlign: msg.sender === "user" ? "right" : "left",
            }}
          >
            {msg.text}
          </div>
        ))}
      </div>

      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        onKeyPress={handleKeyPress}
        placeholder="Type a message"
        style={{ width: "80%", padding: 8 }}
      />
      <button onClick={sendMessage} style={{ padding: "8px 12px", marginLeft: 5 }}>
        Send
      </button>
    </div>
  );
};

export default CompanyChat;
