// src/pages/CompanyChatPage.jsx
import React, { useState, useEffect } from "react";
import { companyService, companyChatService } from "../services/api";

const CompanyChat = () => {
  const [companies, setCompanies] = useState([]);
  const [selectedCompany, setSelectedCompany] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
// ŸÖÿ´ÿßŸÑ ŸÅŸä App.js
const [company, setCompany] = useState(null);

// ÿπŸÜÿØ ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ company
{company && <h1>{company.name}</h1>}

// ÿ£Ÿà
<h1>{company?.name || "Loading..."}</h1>

  // ÿ¨ŸÑÿ® ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ¥ÿ±ŸÉÿßÿ™ ÿπŸÜÿØ ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ
  useEffect(() => {
    const fetchCompanies = async () => {
      try {
        const res = await companyService.getCompany();
        setCompanies(res);
      } catch (err) {
        console.error("Error fetching companies:", err);
      }
    };
    fetchCompanies();
  }, []);

  // ÿ¨ŸÑÿ® ÿßŸÑÿ±ÿ≥ÿßÿ¶ŸÑ ÿπŸÜÿØ ÿßÿÆÿ™Ÿäÿßÿ± ÿ¥ÿ±ŸÉÿ©
  useEffect(() => {
    if (!selectedCompany) return;
    const fetchMessages = async () => {
      try {
        const res = await companyChatService.getMessages(selectedCompany._id);
        setMessages(res);
      } catch (err) {
        console.error("Error fetching messages:", err);
      }
    };
    fetchMessages();
  }, [selectedCompany]);

  const sendMessage = async () => {
    if (!input.trim() || !selectedCompany) return;
    const text = input.trim();
    setMessages(prev => [...prev, { user: "Me", text }]);
    setInput("");

    try {
      const res = await companyChatService.sendAiMessage(selectedCompany._id, text);
      setMessages(prev => [...prev, res]);
    } catch (err) {
      console.error(err);
      setMessages(prev => [...prev, { user: "AI", text: "Error contacting AI" }]);
    }
  };

  return (
    <div style={styles.page}>
      <h1 style={styles.title}>üí¨ Company Chat</h1>

      <div style={styles.selectContainer}>
        <label style={styles.label}>Select a Company:</label>
        <select
          style={styles.select}
          value={selectedCompany?._id || ""}
          onChange={e => {
            const company = companies.find(c => c._id === e.target.value);
            setSelectedCompany(company);
            setMessages([]); // reset messages
          }}
        >
          <option value="">-- Choose Company --</option>
          {companies.map(c => (
            <option key={c._id} value={c._id}>
              {c.name}
            </option>
          ))}
        </select>
      </div>

      {!selectedCompany && <p>Please select a company to start chatting.</p>}

      {selectedCompany && (
        <div style={styles.chatContainer}>
          <div style={styles.messages}>
            {messages.map((m, i) => (
              <div
                key={i}
                style={{
                  ...styles.message,
                  alignSelf: m.user === "Me" ? "flex-end" : "flex-start",
                  backgroundColor: m.user === "Me" ? "#4caf50" : "#f1f0f0",
                  color: m.user === "Me" ? "#fff" : "#000",
                }}
              >
                <strong>{m.user}:</strong> {m.text}
              </div>
            ))}
          </div>

          <div style={styles.inputContainer}>
            <input
              style={styles.input}
              value={input}
              onChange={e => setInput(e.target.value)}
              placeholder="Type a message..."
              onKeyDown={e => e.key === "Enter" && sendMessage()}
            />
            <button style={styles.button} onClick={sendMessage}>Send</button>
          </div>
        </div>
      )}
    </div>
  );
};

const styles = {
  page: {
    maxWidth: "800px",
    margin: "20px auto",
    padding: "10px",
    fontFamily: "Arial, sans-serif",
  },
  title: {
    textAlign: "center",
    marginBottom: "20px",
  },
  selectContainer: {
    marginBottom: "20px",
    display: "flex",
    alignItems: "center",
    gap: "10px",
  },
  label: {
    fontWeight: "bold",
  },
  select: {
    padding: "5px",
    fontSize: "16px",
  },
  chatContainer: {
    display: "flex",
    flexDirection: "column",
    border: "1px solid #ccc",
    borderRadius: "8px",
    padding: "10px",
    height: "500px",
  },
  messages: {
    flex: 1,
    overflowY: "auto",
    marginBottom: "10px",
    display: "flex",
    flexDirection: "column",
    gap: "10px",
  },
  message: {
    padding: "8px 12px",
    borderRadius: "12px",
    maxWidth: "70%",
  },
  inputContainer: {
    display: "flex",
    gap: "10px",
  },
  input: {
    flex: 1,
    padding: "8px",
    borderRadius: "8px",
    border: "1px solid #ccc",
    fontSize: "16px",
  },
  button: {
    padding: "8px 16px",
    borderRadius: "8px",
    border: "none",
    backgroundColor: "#4caf50",
    color: "#fff",
    fontWeight: "bold",
    cursor: "pointer",
  },
};

export default CompanyChat;
