import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: { 'Content-Type': 'application/json' },
});

// إدخال التوكن في كل طلب
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) config.headers.Authorization = `Bearer ${token}`;
    return config;
  },
  (error) => Promise.reject(error)
);

// التعامل مع أخطاء الاستجابة
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// ==========================
// خدمات المصادقة
// ==========================
export const authService = {
  login: async (email, password) => {
    const response = await api.post('/auth/login', { email, password });
    return response.data;
  },
  register: async (name, email, password) => {
    const response = await api.post('/auth/register', { name, email, password });
    return response.data;
  },
};

// ==========================
// خدمات الشركة
// ==========================
export const companyChatService = {
  getMessages: async (companyId) => {
    const res = await api.get(`/companyChat/${companyId}`);
    return res.data;
  },
  sendAiMessage: async (companyId, prompt) => {
    const res = await api.post(`/companyChat/${companyId}/ai`, { prompt });
    return res.data;
  },
};
// ==========================
// خدمات المشاريع
// ==========================
export const projectService = {
  getProjects: async () => {
    const response = await api.get('/projects');
    return response.data;
  },
  createProject: async (projectData) => {
    const response = await api.post('/projects', projectData);
    return response.data;
  },
  updateProject: async (id, projectData) => {
    const response = await api.put(`/projects/${id}`, projectData);
    return response.data;
  },
  deleteProject: async (id) => {
    const response = await api.delete(`/projects/${id}`);
    return response.data;
  },
};

// ==========================
// خدمات AI Chat (مع companyApiKey)
// ==========================
export const publicCompanyService = {
  sendAiMessage: async (companyApiKey, prompt) => {
    try {
      const res = await api.post("/public/chat", { companyApiKey, prompt });
      if (res.data.success) return res.data.reply;
      throw new Error(res.data.error || "No reply from AI");
    } catch (err) {
      console.error("Send AI message error:", err.response?.data || err.message);
      throw err;
    }
  },
};

export default api;
