import React, { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';

const CompanyChat = () => {
  const { companyId } = useParams();
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [tempKey, setTempKey] = useState('');
  const messagesEndRef = useRef(null);

  // تمرير focus للرسائل الجديدة
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  // جلب مفتاح مؤقت للدردشة
  useEffect(() => {
    async function fetchTempKey() {
      try {
        const res = await fetch(`http://localhost:5000/api/public/temp-key/${companyId}`);
        if (!res.ok) throw new Error(`Server responded with ${res.status}`);
        const data = await res.json();
        if (data.success) setTempKey(data.tempKey);
        else console.error('Temp key not generated:', data.error);
      } catch (err) {
        console.error('Failed to fetch temp key:', err.message);
      }
    }
    fetchTempKey();
  }, [companyId]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const sendMessage = async () => {
    if (!input.trim() || !tempKey) return;

    setMessages((prev) => [...prev, { from: 'user', text: input }]);
    const msg = input;
    setInput('');

    try {
      const res = await fetch(`http://localhost:5000/api/chat/ai`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ companyApiKey: tempKey, prompt: msg }),
      });

      if (!res.ok) throw new Error(`Server responded with ${res.status}`);
      const data = await res.json();
      setMessages((prev) => [...prev, { from: 'bot', text: data.reply || 'No reply' }]);
    } catch (err) {
      setMessages((prev) => [...prev, { from: 'bot', text: 'Error contacting API' }]);
      console.error(err.message);
    }
  };

  return (
    <div style={{ minHeight: '100vh', background: '#0b1117', display: 'flex', flexDirection: 'column', alignItems: 'center', padding: '40px', color: '#e6eef8' }}>
      <h1 style={{ marginBottom: '20px', color: '#ff77c6', top: '90px' }}>Company Chat</h1>
      <div style={{ width: '100%', maxWidth: '500px', background: 'rgba(15,20,28,0.8)', borderRadius: '18px', padding: '20px', boxShadow: '0 10px 30px rgba(0,0,0,0.2)', display: 'flex', flexDirection: 'column', gap: '10px' }}>
        <div style={{ flex: 1, overflowY: 'auto', maxHeight: '400px' }}>
          {messages.map((msg, i) => (
            <div key={i} style={{ padding: '10px', borderRadius: '12px', background: msg.from === 'user' ? 'rgba(100,100,255,0.2)' : 'rgba(255,100,100,0.2)', marginBottom: '6px' }}>
              {msg.text}
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>
        <div style={{ display: 'flex', gap: '10px' }}>
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Type your message..."
            style={{ flex: 1, padding: '10px', borderRadius: '12px', border: 'none' }}
            onKeyPress={(e) => { if (e.key === 'Enter') sendMessage(); }}
          />
          <button onClick={sendMessage} style={{ padding: '10px 20px', borderRadius: '12px', border: 'none', background: '#667eea', color: '#fff', cursor: 'pointer' }}>Send</button>
        </div>
      </div>
    </div>
  );
};

export default CompanyChat;
