// CompanyChat.jsx
import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { publicCompanyService, companyService } from "../services/api";

const CompanyChat = () => {
  const { companyId } = useParams();
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  const sendMessage = async () => {
    const trimmed = input.trim();
    if (!trimmed) return;

    setMessages((prev) => [...prev, { sender: "user", text: trimmed }]);
    setInput("");

    try {
      const response = await chatService.sendMessage(trimmed, companyId);
      setMessages((prev) => [...prev, { sender: "bot", text: response }]);
    } catch (err) {
      console.error(err);
      setMessages((prev) => [...prev, { sender: "bot", text: "Error contacting AI" }]);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") sendMessage();
  };

  return (
    <div style={{ maxWidth: 600, margin: "0 auto" }}>
      <div
        id="chat-container"
        style={{ border: "1px solid #ccc", padding: 10, height: 400, overflowY: "auto", marginBottom: 10 }}
      >
        {messages.map((msg, idx) => (
          <div key={idx} className={`message ${msg.sender}`} style={{ marginBottom: 5, color: msg.sender === "user" ? "blue" : "green" }}>
            {msg.text}
          </div>
        ))}
      </div>

      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        onKeyPress={handleKeyPress}
        placeholder="Type a message"
        style={{ width: "80%", padding: 8 }}
      />
      <button onClick={sendMessage} style={{ padding: "8px 12px" }}>Send</button>
    </div>
  );
};

export default CompanyChat;
