// CompanyAiPage.jsx
import React, { useState, useEffect } from 'react';
import { companyService } from '../services/api';

const CompanyAiPage = () => {
  const [copiedUrl, setCopiedUrl] = useState(false);
  const [copiedCode, setCopiedCode] = useState(false);
  const [tempKey, setTempKey] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchTempKey = async () => {
      try {
        const response = await companyService.getTempKey(); // استدعاء الـ API اللي يرجع temp-key
        setTempKey(response.tempKey); // أو حسب شكل البيانات اللي بترجعها
      } catch (err) {
        console.error(err);
        setError('Failed to fetch temporary API key');
      } finally {
        setLoading(false);
      }
    };
    fetchTempKey();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  const aiUrl = `${window.location.origin}/company-chat/${tempKey}`;

  const htmlCssJsCode = `
<!-- HTML -->
<div id="chat-container"></div>
<input type="text" id="user-input" placeholder="Type a message" />
<button id="send-btn">Send</button>

<!-- CSS -->
<style>
#chat-container { border:1px solid #ccc; padding:10px; height:300px; overflow-y:auto; }
#user-input { width:80%; padding:8px; }
#send-btn { padding:8px 12px; }
.message { margin-bottom:5px; }
.message.user { color: blue; }
.message.bot { color: green; }
</style>

<!-- JS -->
<script>
const chatContainer = document.getElementById('chat-container');
const input = document.getElementById('user-input');
const sendBtn = document.getElementById('send-btn');

sendBtn.addEventListener('click', async () => {
  const message = input.value.trim();
  if(!message) return;
  appendMessage('user', message);
  input.value = '';
  try {
    const res = await fetch('${aiUrl}', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt: message })
    });
    const data = await res.json();
    appendMessage('bot', data.reply || 'No reply');
  } catch(err) {
    appendMessage('bot', 'Error contacting AI');
  }
});

function appendMessage(sender, text) {
  const div = document.createElement('div');
  div.className = 'message ' + sender;
  div.textContent = text;
  chatContainer.appendChild(div);
  chatContainer.scrollTop = chatContainer.scrollHeight;
}
</script>
`;

  const handleCopyUrl = () => {
    navigator.clipboard.writeText(aiUrl);
    setCopiedUrl(true);
    setTimeout(() => setCopiedUrl(false), 2000);
  };

  const handleCopyCode = () => {
    navigator.clipboard.writeText(htmlCssJsCode);
    setCopiedCode(true);
    setTimeout(() => setCopiedCode(false), 2000);
  };

  return (
    <div className="card">
      <h2>Company AI Integration</h2>
      
      <div style={{ marginBottom: '20px' }}>
        <label>Company AI URL:</label>
        <div style={{ display: 'flex', gap: '10px', marginTop: '5px' }}>
          <input type="text" readOnly value={aiUrl} className="form-input" />
          <button className="btn btn-secondary" onClick={handleCopyUrl}>
            {copiedUrl ? 'Copied!' : 'Copy'}
          </button>
        </div>
      </div>

      <div>
        <label>HTML / CSS / JS Code for Chat Page:</label>
        <textarea
          readOnly
          value={htmlCssJsCode}
          rows={20}
          style={{ width: '100%', fontFamily: 'monospace', marginTop: '5px' }}
        />
        <button className="btn btn-secondary" style={{ marginTop: '10px' }} onClick={handleCopyCode}>
          {copiedCode ? 'Copied!' : 'Copy Code'}
        </button>
      </div>
    </div>
  );
};

export default CompanyAiPage;
