import React, { useState } from "react";
import axios from "axios";

function ChatInterface() {
  const [apiKey, setApiKey] = useState("");
  const [company, setCompany] = useState(null);
  const [prompt, setPrompt] = useState("");
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);

  // ‚úÖ ÿ¨ŸÑÿ® ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ÿ±ŸÉÿ© ÿπŸÜÿØ ÿ•ÿØÿÆÿßŸÑ ÿßŸÑŸÄ API Key
  const handleLoadCompany = async () => {
    if (!apiKey.trim()) {
      alert("ŸÖŸÜ ŸÅÿ∂ŸÑŸÉ ÿ£ÿØÿÆŸÑ ŸÖŸÅÿ™ÿßÿ≠ API ÿßŸÑÿÆÿßÿµ ÿ®ÿßŸÑÿ¥ÿ±ŸÉÿ©");
      return;
    }

    try {
      const res = await axios.get(
        `http://localhost:5000/api/public/company/${apiKey}`
      );
      if (res.data.success) {
        setCompany(res.data.company);
      } else {
        alert("ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ÿ±ŸÉÿ©!");
      }
    } catch (err) {
      console.error("‚ùå Fetch company error:", err);
      alert("ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ÿ±ŸÉÿ©");
    }
  };

  // ‚úÖ ÿ•ÿ±ÿ≥ÿßŸÑ ÿ±ÿ≥ÿßŸÑÿ© ŸÑŸÑÿ∞ŸÉÿßÿ° ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä
  const handleSend = async () => {
    if (!prompt.trim()) return;
    if (!company) {
      alert("ÿßŸÑÿ±ÿ¨ÿßÿ° ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ÿ±ŸÉÿ© ÿ£ŸàŸÑÿßŸã!");
      return;
    }

    const userMessage = { role: "user", content: prompt };
    setMessages([...messages, userMessage]);
    setPrompt("");
    setLoading(true);

    try {
      const res = await axios.post("http://localhost:5000/api/public/chat", {
        companyApiKey: apiKey,
        prompt,
      });

      if (res.data.success) {
        const aiMessage = { role: "assistant", content: res.data.reply };
        setMessages((prev) => [...prev, userMessage, aiMessage]);
      } else {
        alert("ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑŸÖÿ≠ÿßÿØÿ´ÿ©");
      }
    } catch (err) {
      console.error("‚ùå Chat send error:", err);
      alert("ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿ∞ŸÉÿßÿ° ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-6 max-w-3xl mx-auto bg-white rounded-2xl shadow-lg">
      <h1 className="text-2xl font-bold mb-4 text-center">
        üß† AI Chat Interface
      </h1>

      {/* ÿ•ÿØÿÆÿßŸÑ ŸÖŸÅÿ™ÿßÿ≠ ÿßŸÑÿ¥ÿ±ŸÉÿ© */}
      {!company ? (
        <div className="mb-4">
          <label className="block text-gray-700 mb-2">
            üîë ÿ£ÿØÿÆŸÑ ŸÖŸÅÿ™ÿßÿ≠ API ÿßŸÑÿÆÿßÿµ ÿ®ÿ¥ÿ±ŸÉÿ™ŸÉ:
          </label>
          <input
            type="text"
            placeholder="ŸÖÿ´ÿßŸÑ: sk-123abc..."
            value={apiKey}
            onChange={(e) => setApiKey(e.target.value)}
            className="border border-gray-300 rounded-lg p-2 w-full"
          />
          <button
            onClick={handleLoadCompany}
            className="bg-blue-500 text-white rounded-lg px-4 py-2 mt-3 w-full"
          >
            ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ÿ±ŸÉÿ©
          </button>
        </div>
      ) : (
        <>
          {/* ÿπÿ±ÿ∂ ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑÿ¥ÿ±ŸÉÿ© */}
          <div className="bg-gray-100 p-4 rounded-lg mb-4">
            <h2 className="text-lg font-semibold">{company.name}</h2>
            <p className="text-sm text-gray-600">
              {company.description || "ŸÑÿß ŸäŸàÿ¨ÿØ ŸàÿµŸÅ"}
            </p>
          </div>

          {/* ÿßŸÑŸÖÿ≠ÿßÿØÿ´ÿ© */}
          <div className="border rounded-lg p-4 h-96 overflow-y-auto mb-4 bg-gray-50">
            {messages.map((msg, i) => (
              <div
                key={i}
                className={`mb-2 ${
                  msg.role === "user" ? "text-right" : "text-left"
                }`}
              >
                <span
                  className={`inline-block px-3 py-2 rounded-xl ${
                    msg.role === "user"
                      ? "bg-blue-500 text-white"
                      : "bg-gray-200 text-gray-800"
                  }`}
                >
                  {msg.content}
                </span>
              </div>
            ))}
            {loading && <p className="text-gray-400">‚è≥ ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ±ÿØ...</p>}
          </div>

          {/* ÿ•ÿØÿÆÿßŸÑ ÿßŸÑÿ±ÿ≥ÿßŸÑÿ© */}
          <div className="flex gap-2">
            <input
              type="text"
              placeholder="ÿßŸÉÿ™ÿ® ÿ±ÿ≥ÿßŸÑÿ™ŸÉ ŸáŸÜÿß..."
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
              className="flex-1 border border-gray-300 rounded-lg p-2"
            />
            <button
              onClick={handleSend}
              className="bg-green-500 text-white px-4 py-2 rounded-lg"
              disabled={loading}
            >
              ÿ•ÿ±ÿ≥ÿßŸÑ
            </button>
          </div>
        </>
      )}
    </div>
  );
}

export default ChatInterface;
