// CompanyChat.jsx
import React, { useState, useEffect, useRef } from "react";
import { useParams } from "react-router-dom";
import { publicCompanyService } from "../services/api";

const CompanyChat = () => {
  const { companyId } = useParams();
  const [company, setCompany] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const chatContainerRef = useRef();

  // جلب بيانات الشركة عند التحميل
  useEffect(() => {
    const fetchCompany = async () => {
      try {
        const data = await publicCompanyService.getCompanyByApiKey(companyId);
        setCompany(data);
      } catch (err) {
        console.error("Fetch company error:", err);
        setCompany({ name: "Unknown Company" });
      }
    };
    fetchCompany();
  }, [companyId]);

  // تمرير الرسائل للـ chat container تلقائياً
  useEffect(() => {
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [messages]);

  const sendMessage = async () => {
    const trimmed = input.trim();
    if (!trimmed) return;

    // عرض رسالة المستخدم فوراً
    setMessages((prev) => [...prev, { sender: "user", text: trimmed }]);
    setInput("");

    try {
      const reply = await publicCompanyService.sendAiMessage(companyId, trimmed);
      setMessages((prev) => [...prev, { sender: "bot", text: reply }]);
    } catch (err) {
      console.error("Send AI message error:", err);
      setMessages((prev) => [...prev, { sender: "bot", text: "Error contacting AI" }]);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") sendMessage();
  };

  if (!company) return <p>Loading company...</p>;

  return (
    <div style={{ maxWidth: 600, margin: "20px auto", fontFamily: "sans-serif" }}>
      <h2 style={{ textAlign: "center" }}>{company.name} Chat</h2>

      <div
        ref={chatContainerRef}
        style={{
          border: "1px solid #ccc",
          padding: 10,
          height: 400,
          overflowY: "auto",
          marginBottom: 10,
          borderRadius: 5,
          backgroundColor: "#f9f9f9",
        }}
      >
        {messages.map((msg, idx) => (
          <div
            key={idx}
            style={{
              marginBottom: 8,
              padding: 6,
              borderRadius: 5,
              maxWidth: "80%",
              alignSelf: msg.sender === "user" ? "flex-end" : "flex-start",
              backgroundColor: msg.sender === "user" ? "#d0e7ff" : "#e2ffe2",
            }}
          >
            {msg.text}
          </div>
        ))}
      </div>

      <div style={{ display: "flex", gap: 5 }}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Type a message"
          style={{ flex: 1, padding: 8, borderRadius: 5, border: "1px solid #ccc" }}
        />
        <button onClick={sendMessage} style={{ padding: "8px 12px", borderRadius: 5 }}>
          Send
        </button>
      </div>
    </div>
  );
};

export default CompanyChat;
