import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// إدخال التوكن في كل طلب
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

// التعامل مع أخطاء الاستجابة
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// خدمات المصادقة
export const authService = {
  login: async (email, password) => {
    const response = await api.post('/auth/login', { email, password });
    return response.data;
  },

  register: async (name, email, password) => {
    const response = await api.post('/auth/register', { name, email, password });
    return response.data;
  },
};

// خدمات الشركة
export const companyService = {
  // 🔹 جلب بيانات الشركة بالـ API Key
  getCompanyByApiKey: async (apiKey) => {
    const response = await api.get(`/public/company/${apiKey}`);
    return response.data.company;
  },

  // 🔹 جلب بيانات الشركة بالـ ID (للتوافق مع route الجديد /company/id/:companyId)
  getCompanyById: async (companyId) => {
    const response = await api.get(`/public/company/id/${companyId}`);
    return response.data.company;
  },

  // 🔹 إنشاء أو تحديث بيانات الشركة (المسار الصحيح من الـ backend)
  updateCompany: async (companyData) => {
    const response = await api.post('/company', companyData);
    return response.data;
  },

  // 🔹 جلب جميع الطلبات الخاصة بالشركة
  getRequests: async () => {
    const response = await api.get('/company/requests');
    return response.data;
  },

  // 🔹 حذف طلب محدد حسب الفهرس
  deleteRequest: async (index) => {
    const response = await api.delete(`/company/requests/${index}`);
    return response.data;
  },

  // 🔹 جلب مفتاح مؤقت للشركة
  getTempKey: async (companyId) => {
    try {
      const res = await api.get(`/public/temp-key/${companyId}`);
      if (res.data.success) return res.data.tempKey;
      throw new Error(res.data.error || "Failed to get temp key");
    } catch (err) {
      console.error("Temp key fetch error:", err.response?.status, err.response?.data || err.message);
      throw err;
    }
  },

  // 🔹 إرسال طلب خارجي (يُستخدم من تطبيقات أو مواقع خارجية بالـ API Key)
  submitExternalRequest: async (apiKey, customerName, product, message) => {
    const response = await api.post('/company/external-request', {
      apiKey,
      customerName,
      product,
      message,
    });
    return response.data;
  },
};

// خدمات المشاريع
export const projectService = {
  getProjects: async () => {
    const response = await api.get('/projects');
    return response.data;
  },

  createProject: async (projectData) => {
    const response = await api.post('/projects', projectData);
    return response.data;
  },

  updateProject: async (id, projectData) => {
    const response = await api.put(`/projects/${id}`, projectData);
    return response.data;
  },

  deleteProject: async (id) => {
    const response = await api.delete(`/projects/${id}`);
    return response.data;
  },
};

// خدمات الدردشة
export const chatService = {
  // 🔹 إرسال رسالة إلى AI باستخدام مفتاح الشركة
  sendMessage: async (message, apiKey) => {
    const response = await api.post('/public/chat', { prompt: message, companyApiKey: apiKey });
    return response.data.reply || 'لم يتم تلقي رد';
  },

  // 🔹 جلب سجل الدردشة العام للشركة بالـ ID
  getChatHistory: async (companyId) => {
    const response = await api.get(`/public/chat/${companyId}`);
    return response.data.messages || [];
  },

  // 🔹 إرسال رسالة عامة للشركة بالـ ID
  postChatMessage: async (companyId, user, text) => {
    const response = await api.post(`/public/chat/${companyId}`, { user, text });
    return response.data.message;
  },
};

export default api;
