import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const CompanyChat = () => {
  const { companyId } = useParams();
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [company, setCompany] = useState(null);

  useEffect(() => {
    async function fetchCompany() {
      try {
        const res = await fetch(`http://localhost:5000/api/public/company/${companyId}`);
        const data = await res.json();
        if (data.success) setCompany(data.company);
      } catch (err) {
        console.error(err);
      }
    }
    fetchCompany();
  }, [companyId]);

  const sendMessage = async () => {
    if (!input.trim()) return;
    setMessages([...messages, { from: 'user', text: input }]);
    const msg = input;
    setInput('');

    try {
      const res = await fetch(`http://localhost:5000/api/public/chat/ai`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ companyApiKey: companyId, prompt: msg })
      });
      const data = await res.json();
      setMessages((prev) => [...prev, { from: 'bot', text: data.reply || 'No reply' }]);
    } catch (err) {
      setMessages((prev) => [...prev, { from: 'bot', text: 'Error contacting API' }]);
      console.error(err);
    }
  };

  return (
    <div style={{ minHeight: '100vh', background: '#0b1117', display: 'flex', flexDirection: 'column', alignItems: 'center', padding: '40px', color: '#e6eef8' }}>
      <h1 style={{ marginBottom: '20px', color: '#ff77c6' }}>{company?.name || 'Company Chat'}</h1>
      <p style={{ marginBottom: '20px', color: '#a0b0c0' }}>{company?.description}</p>
      
      <div style={{ width: '100%', maxWidth: '500px', background: 'rgba(15,20,28,0.8)', borderRadius: '18px', padding: '20px', boxShadow: '0 10px 30px rgba(0,0,0,0.2)', display: 'flex', flexDirection: 'column', gap: '10px' }}>
        <div style={{ flex: 1, overflowY: 'auto', maxHeight: '400px' }}>
          {messages.map((msg, i) => (
            <div key={i} style={{ padding: '10px', borderRadius: '12px', background: msg.from === 'user' ? 'rgba(100,100,255,0.2)' : 'rgba(255,100,100,0.2)', marginBottom: '6px' }}>
              {msg.text}
            </div>
          ))}
        </div>
        <div style={{ display: 'flex', gap: '10px' }}>
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Type your message..."
            style={{ flex: 1, padding: '10px', borderRadius: '12px', border: 'none' }}
            onKeyPress={(e) => { if (e.key === 'Enter') sendMessage(); }}
          />
          <button onClick={sendMessage} style={{ padding: '10px 20px', borderRadius: '12px', border: 'none', background: '#667eea', color: '#fff', cursor: 'pointer' }}>Send</button>
        </div>
      </div>
    </div>
  );
};

export default CompanyChat;
