import React, { useState } from "react";
import axios from "axios";
import "./ChatInterface.css";

const ChatInterface = () => {
  const [apiKey, setApiKey] = useState(localStorage.getItem("companyApiKey") || "");
  const [company, setCompany] = useState(null);
  const [prompt, setPrompt] = useState("");
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchCompany = async () => {
    if (!apiKey.trim()) {
      alert("من فضلك أدخل مفتاح API الخاص بالشركة");
      return;
    }

    try {
      const res = await axios.get(`http://localhost:5000/api/public/company/${apiKey}`);
      if (res.data.success) {
        setCompany(res.data.company);
        localStorage.setItem("companyApiKey", apiKey);
      } else {
        alert("لم يتم العثور على بيانات الشركة");
      }
    } catch (err) {
      console.error("❌ Fetch company error:", err);
      alert("حدث خطأ أثناء تحميل بيانات الشركة");
    }
  };

  const handleSend = async (e) => {
    e.preventDefault();
    if (!prompt.trim()) return;
    if (!company) {
      alert("الرجاء تحميل بيانات الشركة أولاً!");
      return;
    }

    const userMsg = { role: "user", content: prompt };
    setMessages((prev) => [...prev, userMsg]);
    setPrompt("");
    setLoading(true);

    try {
      const res = await axios.post("http://localhost:5000/api/public/chat", {
        companyApiKey: apiKey,
        prompt,
      });

      const aiMsg = {
        role: "assistant",
        content: res.data.reply || "لم يتم الحصول على رد من الذكاء الاصطناعي.",
      };
      setMessages((prev) => [...prev, aiMsg]);
    } catch (err) {
      console.error("❌ Chat error:", err);
      const errMsg = {
        role: "assistant",
        content: "حدث خطأ أثناء التواصل مع الخادم.",
      };
      setMessages((prev) => [...prev, errMsg]);
    } finally {
      setLoading(false);
    }
  };

  const handleLogoutCompany = () => {
    localStorage.removeItem("companyApiKey");
    setCompany(null);
    setMessages([]);
    setApiKey("");
  };

  return (
    <div className="chat-container">
      <div className="chat-box">
        <h1 className="chat-header">🤖 AI Chat Assistant</h1>

        {!company ? (
          <div className="api-section">
            <input
              type="text"
              placeholder="أدخل مفتاح API الخاص بشركتك..."
              value={apiKey}
              onChange={(e) => setApiKey(e.target.value)}
            />
            <button onClick={fetchCompany}>تحميل بيانات الشركة</button>
          </div>
        ) : (
          <>
            <div className="company-info">
              <div>
                <h2>{company.name}</h2>
                <p>{company.description || "لا يوجد وصف للشركة"}</p>
              </div>
              <button onClick={handleLogoutCompany} className="logout-btn">
                🔒 تسجيل خروج
              </button>
            </div>

            <div className="chat-messages">
              {messages.length === 0 ? (
                <p className="text-center text-gray-400 mt-36">
                  ابدأ المحادثة الآن مع {company.name}
                </p>
              ) : (
                messages.map((msg, i) => (
                  <div key={i} className={`message ${msg.role}`}>
                    <div className="message-bubble">{msg.content}</div>
                  </div>
                ))
              )}
              {loading && <p className="text-center text-gray-400 mt-2">⏳ جاري الرد...</p>}
            </div>

            <form onSubmit={handleSend} className="chat-input">
              <input
                type="text"
                placeholder="اكتب رسالتك هنا..."
                value={prompt}
                onChange={(e) => setPrompt(e.target.value)}
              />
              <button type="submit" disabled={loading || !prompt.trim()}>
                إرسال
              </button>
            </form>
          </>
        )}
      </div>
    </div>
  );
};

export default ChatInterface;
