import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { companyService } from '../services/api';
import { useNavigate } from 'react-router-dom';
// Components (Keep these if you have actual implementations, otherwise these are placeholders)
// import ProjectManagement from './ProjectManagement';
// import ChatInterface from './ChatInterface';
// import CompanyDashboard from './CompanyDashboard';
import './Dashboard.css'; // تأكد من استيراد ملف الـ CSS الخاص بك هنا

// Placeholder components for other tabs - تم تحديثها لتناسب الأنماط الجديدة
const ChatInterface = () => (
  <div className="card text-center">
    <h2>واجهة الدردشة قريباً...</h2>
    <p>هنا سيتم دمج واجهة الدردشة التفاعلية مع العملاء والذكاء الاصطناعي.</p>
  </div>
);
const CompanyDashboard = () => (
  <div className="card text-center">
    <h2>لوحة تحكم الذكاء الاصطناعي قريباً...</h2>
    <p>تابع أداء الذكاء الاصطناعي الخاص بشركتك، وإحصائيات التفاعل والتحسينات.</p>
  </div>
);
// نهاية المكونات الوهمية

const Dashboard = () => {
  const [company, setCompany] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [activeTab, setActiveTab] = useState('overview'); // Set initial active tab

  const { user, logout } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    fetchCompanyData();
  }, []);

  const fetchCompanyData = async () => {
    try {
      // Simulate API delay
      // await new Promise(resolve => setTimeout(resolve, 1000));
      const companyData = await companyService.getCompany();
      setCompany(companyData);
    } catch (error) {
      if (error.response?.status === 404) {
        setError('لم يتم العثور على بيانات الشركة. يرجى إكمال إعداد الشركة أولاً.');
      } else {
        setError('حدث خطأ أثناء تحميل بيانات الشركة');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  if (loading) {
    return (
      <div className="dashboard-wrapper">
        <div className="loading">
          <div className="spinner"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-wrapper">
      {/* Navigation Tabs - fixed at the top */}
      <div className="dashboard-nav">
        {[
          { key: 'overview', label: 'نظرة عامة' },
          { key: 'company', label: 'بيانات الشركة' },
          { key: 'requests', label: 'طلبات العملاء' },
          { key: 'chat', label: 'الدردشة' },
          { key: 'company-dashboard', label: 'لوحة تحكم الذكاء الاصطناعي' },
          { key: 'api', label: 'API Key' },
        ].map((tab) => (
          <button
            key={tab.key}
            onClick={() => setActiveTab(tab.key)}
            className={`btn ${
              activeTab === tab.key ? 'btn-primary' : 'btn-secondary'
            }`}
          >
            {tab.label}
          </button>
        ))}
      </div>

      {/* Main Content Area */}
      <div className="dashboard-content container"> {/* Use container for max-width */}
        {error ? (
          <div className="card text-center error-message">
            <h2>خطأ</h2>
            <p>{error}</p>
            <button
              onClick={() => navigate('/company-setup')}
              className="btn btn-primary"
            >
              إعداد الشركة
            </button>
          </div>
        ) : (
          <>
            {/* Tab Content */}
            {activeTab === 'overview' && (
              <div className="grid grid-3">
                <InfoCard title="اسم الشركة" value={company?.name || 'غير محدد'} />
                <InfoCard title="القطاع" value={company?.industry || 'غير محدد'} />
                <InfoCard
                  title="عدد الطلبات"
                  value={company?.requests?.length || 0}
                />
              </div>
            )}

            {activeTab === 'company' && (
              <CompanyInfo company={company} onUpdate={fetchCompanyData} />
            )}

            {activeTab === 'requests' && (
              <CustomerRequests company={company} onUpdate={fetchCompanyData} />
            )}

            {activeTab === 'chat' && <ChatInterface />}

            {activeTab === 'company-dashboard' && <CompanyDashboard />}

            {activeTab === 'api' && <ApiKeyInfo company={company} />}
          </>
        )}
      </div>
    </div>
  );
};

// Re-usable InfoCard component
const InfoCard = ({ title, value }) => (
  <div className="card text-center">
    <h3>{title}</h3>
    <p>{value}</p>
  </div>
);

// --- Company Info ---
const CompanyInfo = ({ company, onUpdate }) => {
  const [formData, setFormData] = useState({
    name: company?.name || '',
    description: company?.description || '',
    industry: company?.industry || '',
    size: company?.size || '',
    vision: company?.vision || '',
    mission: company?.mission || '',
    values: company?.values || [],
  });
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    if (company) {
      setFormData({
        name: company.name || '',
        description: company.description || '',
        industry: company.industry || '',
        size: company.size || '',
        vision: company.vision || '',
        mission: company.mission || '',
        values: company.values || [],
      });
    }
  }, [company]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleValuesChange = (e) => {
    const values = e.target.value
      .split(',')
      .map((v) => v.trim())
      .filter((v) => v);
    setFormData({ ...formData, values });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setSuccess('');
    setError('');
    try {
      await companyService.updateCompany(formData);
      setSuccess('تم تحديث بيانات الشركة بنجاح');
      onUpdate();
    } catch (err) {
      console.error('Error updating company:', err);
      setError('فشل تحديث بيانات الشركة. يرجى المحاولة مرة أخرى.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="card">
      <h2>بيانات الشركة</h2>
      <form onSubmit={handleSubmit}>
        <div className="grid grid-2">
          <InputField label="اسم الشركة" name="name" value={formData.name} onChange={handleChange} />
          <InputField label="القطاع" name="industry" value={formData.industry} onChange={handleChange} />
        </div>
        <TextAreaField label="وصف الشركة" name="description" value={formData.description} onChange={handleChange} />
        <SelectField label="حجم الشركة" name="size" value={formData.size} onChange={handleChange} />
        <TextAreaField label="الرؤية" name="vision" value={formData.vision} onChange={handleChange} />
        <TextAreaField label="الرسالة" name="mission" value={formData.mission} onChange={handleChange} />
        <InputField label="القيم (مفصولة بفواصل)" name="values" value={formData.values.join(', ')} onChange={handleValuesChange} />
        {success && <div className="success">{success}</div>}
        {error && <div className="error">{error}</div>}
        <button type="submit" className="btn btn-primary" disabled={loading}>
          {loading ? 'جاري الحفظ...' : 'حفظ التغييرات'}
        </button>
      </form>
    </div>
  );
};

// Re-usable InputField component
const InputField = ({ label, name, value, onChange, type = 'text' }) => (
  <div className="form-group">
    <label htmlFor={name} className="form-label">{label}</label>
    <input type={type} id={name} name={name} className="form-input" value={value} onChange={onChange} />
  </div>
);

// Re-usable TextAreaField component
const TextAreaField = ({ label, name, value, onChange, rows = "3" }) => (
  <div className="form-group">
    <label htmlFor={name} className="form-label">{label}</label>
    <textarea id={name} name={name} className="form-input" value={value} onChange={onChange} rows={rows} />
  </div>
);

// Re-usable SelectField component
const SelectField = ({ label, name, value, onChange }) => (
  <div className="form-group">
    <label htmlFor={name} className="form-label">{label}</label>
    <select id={name} name={name} className="form-input" value={value} onChange={onChange}>
      <option value="">اختر حجم الشركة</option>
      <option value="startup">ناشئة (1-10 موظف)</option>
      <option value="small">صغيرة (11-50 موظف)</option>
      <option value="medium">متوسطة (51-200 موظف)</option>
      <option value="large">كبيرة (200+ موظف)</option>
    </select>
  </div>
);

// --- Customer Requests ---
const CustomerRequests = ({ company, onUpdate }) => {
  const [requests, setRequests] = useState(company?.requests || []);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    if (company?.requests) {
      setRequests(company.requests);
    }
  }, [company?.requests]);

  const handleDeleteRequest = async (index) => {
    if (!window.confirm('هل أنت متأكد من حذف هذا الطلب؟')) return;
    setLoading(true);
    setError('');
    try {
      // Simulate API call for deletion
      // In a real app, you'd send an index or ID to the backend
      // await companyService.deleteRequest(index);
      const updatedRequests = requests.filter((_, i) => i !== index);
      setRequests(updatedRequests);
      // Assuming onUpdate fetches updated company data including requests from backend
      onUpdate();
    } catch (err) {
      console.error('Error deleting request:', err);
      setError('فشل حذف الطلب. يرجى المحاولة مرة أخرى.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="card">
      <h2>طلبات العملاء</h2>
      {error && <div className="error mb-20">{error}</div>}
      {requests.length === 0 ? (
        <p className="text-center">
          لا توجد طلبات حالياً
        </p>
      ) : (
        requests.map((req, i) => (
          <div key={i} className="request-item card"> {/* Added card class for consistent styling */}
            <h4>{req.customerName}</h4>
            <p><strong>المنتج:</strong> {req.product}</p>
            <p><strong>الرسالة:</strong> {req.message}</p>
            <button onClick={() => handleDeleteRequest(i)} className="btn btn-secondary" disabled={loading}>حذف</button>
          </div>
        ))
      )}
    </div>
  );
};

// --- API Key Info ---
const ApiKeyInfo = ({ company }) => {
  const [copied, setCopied] = useState(false);
  const copyToClipboard = () => {
    navigator.clipboard.writeText(company?.apiKey || '');
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  return (
    <div className="card">
      <h2>مفتاح API</h2>
      <div className="form-group">
        <label className="form-label">مفتاح API الخاص بشركتك</label>
        <div className="api-key-display">
          <input type="text" className="form-input" value={company?.apiKey || '********************'} readOnly />
          <button onClick={copyToClipboard} className="btn btn-secondary">
            {copied ? 'تم النسخ!' : 'نسخ'}
          </button>
        </div>
        {!company?.apiKey && <p className="error" style={{marginTop: '10px'}}>لا يوجد مفتاح API. يرجى الاتصال بالدعم لإنشائه.</p>}
      </div>
    </div>
  );
};

export default Dashboard;
