import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { useAuth } from '../context/AuthContext';
import { useI18n } from '../context/I18nContext';
import { useTheme } from '../context/ThemeContext';

const Navbar = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { isAuthenticated, logout } = useAuth();
  const { t } = useI18n();
  const { isDark } = useTheme();

  const isActive = (path) =>
    location.pathname === path ? { color: '#667eea' } : {};

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <motion.nav
      initial={{ y: -20, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.4 }}
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100%',
        height: '0px',
        zIndex: 1000,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        background: isDark
          ? 'rgba(15, 23, 42, 0.9)'
          : 'rgba(255,255,255,0.9)',
        borderBottom: isDark ? '1px solid #1f2937' : '1px solid #ddd',
        backdropFilter: 'saturate(180%) blur(10px)',
      }}
    >
      <div
        style={{
          width: '100%',
          maxWidth: '1200px',
          padding: '0 24px',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between',
        }}
      >
        <Link
          to="/"
          style={{
            textDecoration: 'none',
            color: '#667eea',
            fontWeight: 800,
            fontSize: 22,
            whiteSpace: 'nowrap',
          }}
        >
          AiThor
        </Link>

        <div
          style={{
            display: 'flex',
            alignItems: 'center',
            flexWrap: 'wrap',
            gap: '12px',
            justifyContent: 'flex-end',
          }}
        >
          <Link
            to="/"
            style={{
              textDecoration: 'none',
              color: isDark ? '#e2e8f0' : '#333',
              fontWeight: 600,
              fontSize: 15,
              ...isActive('/'),
            }}
          >
            Home
          </Link>

          {!isAuthenticated ? (
            <>
              <Link
                to="/login"
                style={{
                  textDecoration: 'none',
                  color: isDark ? '#e2e8f0' : '#333',
                  fontWeight: 600,
                  fontSize: 15,
                  ...isActive('/login'),
                }}
              >
                Login
              </Link>

              <Link
                to="/register"
                style={{
                  textDecoration: 'none',
                  color: isDark ? '#e2e8f0' : '#333',
                  fontWeight: 600,
                  fontSize: 15,
                  ...isActive('/register'),
                }}
              >
                Register
              </Link>

              <Link
                to="/dashboard"
                style={{
                  background: '#667eea',
                  color: '#fff',
                  padding: '8px 14px',
                  borderRadius: 8,
                  textDecoration: 'none',
                  fontWeight: 600,
                  fontSize: 14,
                  whiteSpace: 'nowrap',
                }}
              >
                Get Started
              </Link>
            </>
          ) : (
            <>
              <Link
                to="/dashboard"
                style={{
                  textDecoration: 'none',
                  color: isDark ? '#e2e8f0' : '#333',
                  fontWeight: 600,
                  fontSize: 15,
                  ...isActive('/dashboard'),
                }}
              >
                Dashboard
              </Link>

              <button
                onClick={handleLogout}
                style={{
                  background: '#e53e3e',
                  color: '#fff',
                  border: 'none',
                  padding: '8px 12px',
                  borderRadius: 8,
                  cursor: 'pointer',
                  fontWeight: 600,
                  fontSize: 14,
                }}
              >
                Logout
              </button>
            </>
          )}
        </div>
      </div>
    </motion.nav>
  );
};

export default Navbar;
