import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { useAuth } from '../context/AuthContext';
import { useI18n } from '../context/I18nContext';
import { useTheme } from '../context/ThemeContext';

const Navbar = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { isAuthenticated, logout } = useAuth();
  const { t, switchLang, lang } = useI18n();
  const { isDark, toggleTheme } = useTheme();

  const isActive = (path) => location.pathname === path ? { color: '#667eea' } : {};

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <motion.nav
      initial={{ y: -20, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.4 }}
      style={{
        position: 'sticky', top: 0, zIndex: 50,
        backdropFilter: 'saturate(180%) blur(10px)',
        background: isDark ? 'rgba(15, 23, 42, 0.75)' : 'rgba(255,255,255,0.75)',
        borderBottom: isDark ? '1px solid #1f2937' : '1px solid #eee'
      }}
    >
      <div className="container" style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '12px 0' }}>
        <Link to="/" style={{ textDecoration: 'none', color: '#667eea', fontWeight: 800, fontSize: 22 }}>AiThor</Link>
        <div style={{ display: 'flex', gap: 16, alignItems: 'center' }}>
          <Link to="/" style={{ textDecoration: 'none', color: isDark ? '#e2e8f0' : '#495057', fontWeight: 600, ...isActive('/') }}>{t('home')}</Link>
          {!isAuthenticated && (
            <>
              <Link to="/login" style={{ textDecoration: 'none', color: isDark ? '#e2e8f0' : '#495057', fontWeight: 600, ...isActive('/login') }}>{t('login')}</Link>
              <Link to="/register" style={{ textDecoration: 'none', color: isDark ? '#e2e8f0' : '#495057', fontWeight: 600, ...isActive('/register') }}>{t('register')}</Link>
              <Link to="/dashboard" className="btn btn-primary">{t('startNow')}</Link>
            </>
          )}
          {isAuthenticated && (
            <>
              <Link to="/dashboard" style={{ textDecoration: 'none', color: isDark ? '#e2e8f0' : '#495057', fontWeight: 600, ...isActive('/dashboard') }}>{t('dashboard')}</Link>
              <button onClick={handleLogout} className="btn btn-secondary">خروج</button>
            </>
          )}
           </div>
      </div>
    </motion.nav>
  );
};

export default Navbar;
