import React, { useState } from 'react';
import { authService } from '../services/api';

const TestPage = () => {
  const [testResult, setTestResult] = useState('');
  const [loading, setLoading] = useState(false);

  const testConnection = async () => {
    setLoading(true);
    setTestResult('جاري اختبار الاتصال...');
    
    try {
      // اختبار الاتصال بالباك إند
      const response = await fetch('https://aithor2-production.up.railway.app/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: 'test@test.com',
          password: 'test123'
        })
      });
      
      if (response.ok) {
        setTestResult('✅ الاتصال بالباك إند يعمل بشكل صحيح!');
      } else {
        const error = await response.text();
        setTestResult(`⚠️ الباك إند يعمل لكن هناك خطأ: ${error}`);
      }
    } catch (error) {
      setTestResult(`❌ خطأ في الاتصال: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ 
      minHeight: '100vh', 
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'center',
      padding: '20px',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
    }}>
      <div className="card" style={{ width: '100%', maxWidth: '500px' }}>
        <div className="text-center mb-20">
          <h1 style={{ 
            fontSize: '2rem', 
            fontWeight: '700', 
            color: '#667eea',
            marginBottom: '10px'
          }}>
            اختبار الاتصال
          </h1>
          <p style={{ color: '#6c757d' }}>
            اختبار الاتصال بين الفرونت إند والباك إند
          </p>
        </div>

        <div style={{ marginBottom: '20px' }}>
          <button
            onClick={testConnection}
            className="btn btn-primary"
            style={{ width: '100%' }}
            disabled={loading}
          >
            {loading ? 'جاري الاختبار...' : 'اختبار الاتصال'}
          </button>
        </div>

        {testResult && (
          <div style={{
            padding: '15px',
            borderRadius: '8px',
            background: testResult.includes('✅') ? '#d4edda' : 
                       testResult.includes('⚠️') ? '#fff3cd' : '#f8d7da',
            border: testResult.includes('✅') ? '1px solid #c3e6cb' : 
                   testResult.includes('⚠️') ? '1px solid #ffeaa7' : '1px solid #f5c6cb',
            color: testResult.includes('✅') ? '#155724' : 
                   testResult.includes('⚠️') ? '#856404' : '#721c24'
          }}>
            {testResult}
          </div>
        )}

        <div style={{ marginTop: '20px', fontSize: '0.9rem', color: '#6c757d' }}>
          <p><strong>رابط الباك إند:</strong> https://aithor2-production.up.railway.app/api</p>
          <p><strong>رابط الفرونت إند:</strong> http://localhost:3000</p>
        </div>
      </div>
    </div>
  );
};

export default TestPage;
