import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PublicCompanyChat = () => {
  const [company, setCompany] = useState(null);
  const [prompt, setPrompt] = useState('');
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loadingCompany, setLoadingCompany] = useState(true);

  useEffect(() => {
    fetchCompany();
  }, []);

  // جلب بيانات الشركة مع الـ API Key
  const fetchCompany = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/public/company');
      setCompany(res.data.company);
    } catch (err) {
      console.error('خطأ في جلب بيانات الشركة:', err);
    } finally {
      setLoadingCompany(false);
    }
  };

  // إرسال رسالة إلى الذكاء الاصطناعي
  const handleSend = async (e) => {
    e.preventDefault();
    if (!prompt.trim() || !company?.apiKey) return;

    const userMsg = {
      role: 'user',
      content: prompt,
      timestamp: new Date().toISOString(),
    };
    setMessages((prev) => [...prev, userMsg]);
    setPrompt('');
    setLoading(true);

    try {
      const res = await axios.post('http://localhost:5000/api/public/chat', {
        companyApiKey: company.apiKey,
        prompt,
      });

      const aiMsg = {
        role: 'assistant',
        content: res.data.reply || 'لم يتم الحصول على رد من الذكاء الاصطناعي.',
        timestamp: new Date().toISOString(),
      };
      setMessages((prev) => [...prev, aiMsg]);
    } catch (err) {
      const errMsg = {
        role: 'assistant',
        content: 'حدث خطأ أثناء التواصل مع الخادم.',
        timestamp: new Date().toISOString(),
        error: true,
      };
      setMessages((prev) => [...prev, errMsg]);
    } finally {
      setLoading(false);
    }
  };

  const formatTime = (timestamp) =>
    new Date(timestamp).toLocaleTimeString('ar-SA', {
      hour: '2-digit',
      minute: '2-digit',
    });

  if (loadingCompany) {
    return (
      <div style={{ textAlign: 'center', padding: '50px' }}>جاري تحميل بيانات الشركة...</div>
    );
  }

  if (!company) {
    return (
      <div style={{ textAlign: 'center', padding: '50px', color: '#c00' }}>
        لم يتم العثور على بيانات الشركة
      </div>
    );
  }

  return (
    <div
      style={{
        maxWidth: '700px',
        margin: '40px auto',
        padding: '20px',
        border: '1px solid #ddd',
        borderRadius: '10px',
        background: '#f9f9f9',
      }}
    >
      <h2 style={{ textAlign: 'center', color: '#333' }}>
        محادثة الذكاء الاصطناعي - {company.name}
      </h2>

      <div
        style={{
          height: '400px',
          overflowY: 'auto',
          padding: '10px',
          background: '#fff',
          border: '1px solid #ccc',
          borderRadius: '8px',
          marginBottom: '15px',
        }}
      >
        {messages.length === 0 ? (
          <p style={{ textAlign: 'center', color: '#888', marginTop: '150px' }}>
            ابدأ المحادثة الآن بطرح سؤالك حول {company.name}
          </p>
        ) : (
          messages.map((msg, i) => (
            <div
              key={i}
              style={{
                display: 'flex',
                flexDirection: msg.role === 'user' ? 'row-reverse' : 'row',
                marginBottom: '10px',
              }}
            >
              <div
                style={{
                  background: msg.role === 'user' ? '#1976d2' : '#eee',
                  color: msg.role === 'user' ? 'white' : 'black',
                  padding: '10px 15px',
                  borderRadius: '12px',
                  maxWidth: '70%',
                }}
              >
                <p style={{ margin: 0 }}>{msg.content}</p>
                <small style={{ opacity: 0.6 }}>{formatTime(msg.timestamp)}</small>
              </div>
            </div>
          ))
        )}
        {loading && (
          <p style={{ textAlign: 'center', color: '#666' }}>جاري توليد الرد...</p>
        )}
      </div>

      <form onSubmit={handleSend} style={{ display: 'flex', gap: '10px' }}>
        <input
          type="text"
          placeholder="اكتب رسالتك..."
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          disabled={loading}
          style={{
            flex: 1,
            padding: '10px',
            borderRadius: '6px',
            border: '1px solid #ccc',
          }}
        />
        <button
          type="submit"
          disabled={loading || !prompt.trim()}
          style={{
            padding: '10px 20px',
            border: 'none',
            borderRadius: '6px',
            background: '#1976d2',
            color: 'white',
            cursor: 'pointer',
          }}
        >
          إرسال
        </button>
      </form>
    </div>
  );
};

export default PublicCompanyChat;
