import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Navbar = () => {
  const location = useLocation();
  const isActive = (path) => location.pathname === path ? { color: '#667eea' } : {};

  return (
    <nav style={{
      position: 'sticky', top: 0, zIndex: 50,
      backdropFilter: 'saturate(180%) blur(10px)',
      background: 'rgba(255,255,255,0.75)',
      borderBottom: '1px solid #eee'
    }}>
      <div className="container" style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '12px 0' }}>
        <Link to="/" style={{ textDecoration: 'none', color: '#667eea', fontWeight: 800, fontSize: 22 }}>AiThor</Link>
        <div style={{ display: 'flex', gap: 16, alignItems: 'center' }}>
          <Link to="/" style={{ textDecoration: 'none', color: '#495057', fontWeight: 600, ...isActive('/') }}>الرئيسية</Link>
          <Link to="/login" style={{ textDecoration: 'none', color: '#495057', fontWeight: 600, ...isActive('/login') }}>تسجيل الدخول</Link>
          <Link to="/register" style={{ textDecoration: 'none', color: '#495057', fontWeight: 600, ...isActive('/register') }}>إنشاء حساب</Link>
          <Link to="/dashboard" className="btn btn-primary">ابدأ الآن</Link>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
