import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { companyService } from '../services/api';
import { useNavigate } from 'react-router-dom';
import ProjectManagement from './ProjectManagement';
import ChatInterface from './ChatInterface';
import CompanyDashboard from './CompanyDashboard';
import CompanySetup from './CompanySetup';

import './Dashboard.css';

const Dashboard = () => {
  const [company, setCompany] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [activeTab, setActiveTab] = useState('overview');

  const { logout } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    fetchCompanyData();
  }, []);

  const fetchCompanyData = async () => {
    try {
      const companyData = await companyService.getCompany();
      setCompany(companyData);
    } catch (error) {
      if (error.response?.status === 404) {
        setError('لم يتم العثور على بيانات الشركة. يرجى إكمال إعداد الشركة أولاً.');
      } else {
        setError('حدث خطأ أثناء تحميل بيانات الشركة');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
      </div>
    );
  }

  return (
    <div className="dashboard-wrapper">
      {/* === Navbar Tabs في الأعلى === */}
      <div className="dashboard-nav">
        {[
          { key: 'overview', label: 'نظرة عامة' },
          { key: 'company', label: 'بيانات الشركة' },
          { key: 'requests', label: 'طلبات العملاء' },
          { key: 'chat', label: 'الدردشة' },
          { key: 'company-dashboard', label: 'لوحة تحكم الذكاء الاصطناعي' },
          { key: 'api', label: 'API Key' },
        ].map((tab) => (
          <button
            key={tab.key}
            onClick={() => setActiveTab(tab.key)}
            className={`btn ${
              activeTab === tab.key ? 'btn-primary' : 'btn-secondary'
            }`}
          >
            {tab.label}
          </button>
        ))}
      </div>

      {/* === المحتوى أسفل التبويبات === */}
      <div className="dashboard-content container">
        {error ? (
          <div className="card text-center error-message">
            <h2 style={{ color: '#dc3545', marginBottom: '20px' }}>خطأ</h2>
            <p style={{ color: 'var(--text-color-light)', marginBottom: '20px' }}>{error}</p>
            <button onClick={() => navigate('/company-setup')} className="btn btn-primary">
              إعداد الشركة
            </button>
          </div>
        ) : (
          <>
            {activeTab === 'overview' && (
              <div className="grid grid-3">
                <InfoCard title="اسم الشركة" value={company?.name || 'غير محدد'} />
                <InfoCard title="القطاع" value={company?.industry || 'غير محدد'} />
                <InfoCard title="عدد الطلبات" value={company?.requests?.length || 0} />
              </div>
            )}
            {activeTab === 'company' && (
              <CompanyInfo company={company} onUpdate={fetchCompanyData} />
            )}
            {activeTab === 'requests' && (
              <CustomerRequests company={company} onUpdate={fetchCompanyData} />
            )}
            {activeTab === 'chat' && <ChatInterface />}
            {activeTab === 'company-dashboard' && <CompanyDashboard />}
            {activeTab === 'api' && <ApiKeyInfo company={company} />}
          </>
        )}
      </div>
    </div>
  );
};

/* === المكونات المساعدة كما هي === */
const InfoCard = ({ title, value }) => (
  <div className="card text-center">
    <h3>{title}</h3>
    <p style={{ fontSize: '1.2rem', fontWeight: '600' }}>{value}</p>
  </div>
);
const CompanyInfo = ({ company, onUpdate }) => {
  const [formData, setFormData] = useState({
    name: company?.name || '',
    description: company?.description || '',
    industry: company?.industry || '',
    size: company?.size || '',
    vision: company?.vision || '',
    mission: company?.mission || '',
    values: company?.values || [],
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await companyService.updateCompany(formData);
    onUpdate();
  };

  return (
    <div className="card">
      <h2>بيانات الشركة</h2>
      <form onSubmit={handleSubmit}>
        <div className="grid grid-2">
          <div className="form-group">
            <label>اسم الشركة</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              className="form-input"
            />
          </div>
          <div className="form-group">
            <label>القطاع</label>
            <input
              type="text"
              name="industry"
              value={formData.industry}
              onChange={handleChange}
              className="form-input"
            />
          </div>
        </div>
        <div className="form-group">
          <label>الوصف</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            rows="3"
            className="form-input"
          />
        </div>
        <button type="submit" className="btn btn-primary">
          حفظ
        </button>
      </form>
    </div>
  );
};
const CustomerRequests = ({ company, onUpdate }) => {
  const [requests, setRequests] = useState(company?.requests || []);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (company?.requests) {
      setRequests(company.requests);
    }
  }, [company?.requests]);

  const handleDeleteRequest = async (index) => {
    if (!window.confirm('هل أنت متأكد من حذف هذا الطلب؟')) return;
    setLoading(true);
    try {
      await companyService.deleteRequest(index);
      setRequests(requests.filter((_, i) => i !== index));
      onUpdate();
    } catch (error) {
      console.error('Error deleting request:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="card">
      <h2 style={{ marginBottom: '20px' }}>طلبات العملاء</h2>
      {requests.length === 0 ? (
        <p style={{ textAlign: 'center', color: 'rgba(255, 255, 255, 0.7)', padding: '40px' }}>
          لا توجد طلبات حالياً
        </p>
      ) : (
        requests.map((req, i) => (
          <div
            key={i}
            style={{
              background: 'var(--input-bg-dark)',
              padding: '15px',
              marginBottom: '10px',
              borderRadius: '8px',
              border: '1px solid var(--input-border-dark)',
              color: 'var(--text-color-light)',
            }}
          >
            <h4>{req.customerName}</h4>
            <p><strong>المنتج:</strong> {req.product}</p>
            <p><strong>الرسالة:</strong> {req.message}</p>
            <button
              onClick={() => handleDeleteRequest(i)}
              className="btn btn-secondary"
              disabled={loading}
            >
              حذف
            </button>
          </div>
        ))
      )}
    </div>
  );
};
const ApiKeyInfo = ({ company }) => {
  const [copied, setCopied] = useState(false);
  const copyToClipboard = () => {
    navigator.clipboard.writeText(company?.apiKey || '');
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  return (
    <div className="card">
      <h2 style={{ marginBottom: '20px' }}>مفتاح API</h2> {/* Color handled by CSS */}
      <div style={{ marginBottom: '20px' }}>
        <label className="form-label">مفتاح API الخاص بشركتك</label>
        <div style={{ display: 'flex', gap: '10px' }}>
          <input type="text" className="form-input" value={company?.apiKey || ''} readOnly />
          <button onClick={copyToClipboard} className="btn btn-secondary">
            {copied ? 'تم النسخ!' : 'نسخ'}
          </button>
        </div>
      </div>
    </div>
  );
};
export default Dashboard;
