import React, { useState, useEffect } from "react";
import axios from "axios";

const PublicCompanyChat = () => {
  const [company, setCompany] = useState(null);
  const [prompt, setPrompt] = useState("");
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loadingCompany, setLoadingCompany] = useState(true);

  // ‚úÖ ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ API Key ŸÖŸÜ ÿßŸÑÿ±ÿßÿ®ÿ∑ (ŸÖÿ´ŸÑÿßŸã /chat?key=XYZ)
  const searchParams = new URLSearchParams(window.location.search);
  const companyApiKey = searchParams.get("key");

  useEffect(() => {
    if (companyApiKey) fetchCompany(companyApiKey);
    else setLoadingCompany(false);
  }, [companyApiKey]);

  // üè¢ ÿ¨ŸÑÿ® ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ÿ±ŸÉÿ© ÿ®ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑŸÄ API Key
   // ‚úÖ ÿ¨ŸÑÿ® ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ÿ±ŸÉÿ©
   const fetchCompany = async () => {
    try {
      const res = await axios.get(
        `http://localhost:5000/api/public/company/${apiKey}`
      );
      if (res.data.success) {
        setCompany(res.data.company);
      } else {
        alert("ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ÿ±ŸÉÿ©");
      }
    } catch (err) {
      console.error("‚ùå Fetch company error:", err);
      alert("ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ¨ŸÑÿ® ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ÿ±ŸÉÿ©");
    }
  };

  fetchCompany();


  // üí¨ ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ±ÿ≥ÿßŸÑÿ© ÿ•ŸÑŸâ ÿßŸÑÿ∞ŸÉÿßÿ° ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä
  const handleSend = async (e) => {
    e.preventDefault();
    if (!prompt.trim() || !companyApiKey) return;

    const userMsg = {
      role: "user",
      content: prompt,
      timestamp: new Date().toISOString(),
    };
    setMessages((prev) => [...prev, userMsg]);
    setPrompt("");
    setLoading(true);

    try {
      const res = await axios.post("http://localhost:5000/api/public/chat", {
        companyApiKey,
        prompt,
      });

      const aiMsg = {
        role: "assistant",
        content: res.data.reply || "ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿ±ÿØ ŸÖŸÜ ÿßŸÑÿ∞ŸÉÿßÿ° ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä.",
        timestamp: new Date().toISOString(),
      };
      setMessages((prev) => [...prev, aiMsg]);
    } catch (err) {
      const errMsg = {
        role: "assistant",
        content: "ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ™ŸàÿßÿµŸÑ ŸÖÿπ ÿßŸÑÿÆÿßÿØŸÖ.",
        timestamp: new Date().toISOString(),
        error: true,
      };
      setMessages((prev) => [...prev, errMsg]);
    } finally {
      setLoading(false);
    }
  };

  const formatTime = (timestamp) =>
    new Date(timestamp).toLocaleTimeString("ar-SA", {
      hour: "2-digit",
      minute: "2-digit",
    });

  // ‚è≥ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ¥ÿ±ŸÉÿ©
  if (loadingCompany) {
    return <div style={{ textAlign: "center", padding: "50px" }}>ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ÿ±ŸÉÿ©...</div>;
  }

  // üö´ ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ ÿßŸÑÿ¥ÿ±ŸÉÿ©
  if (!company) {
    return (
      <div style={{ textAlign: "center", padding: "50px", color: "#c00" }}>
        ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ÿ±ŸÉÿ©
      </div>
    );
  }

  return (
    <div
      style={{
        maxWidth: "700px",
        margin: "40px auto",
        padding: "20px",
        border: "1px solid #ddd",
        borderRadius: "10px",
        background: "#f9f9f9",
      }}
    >
      <h2 style={{ textAlign: "center", color: "#333" }}>
        ŸÖÿ≠ÿßÿØÿ´ÿ© ÿßŸÑÿ∞ŸÉÿßÿ° ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä - {company.name}
      </h2>

      <div
        style={{
          height: "400px",
          overflowY: "auto",
          padding: "10px",
          background: "#fff",
          border: "1px solid #ccc",
          borderRadius: "8px",
          marginBottom: "15px",
        }}
      >
        {messages.length === 0 ? (
          <p style={{ textAlign: "center", color: "#888", marginTop: "150px" }}>
            ÿßÿ®ÿØÿ£ ÿßŸÑŸÖÿ≠ÿßÿØÿ´ÿ© ÿßŸÑÿ¢ŸÜ ÿ®ÿ∑ÿ±ÿ≠ ÿ≥ÿ§ÿßŸÑŸÉ ÿ≠ŸàŸÑ {company.name}
          </p>
        ) : (
          messages.map((msg, i) => (
            <div
              key={i}
              style={{
                display: "flex",
                flexDirection: msg.role === "user" ? "row-reverse" : "row",
                marginBottom: "10px",
              }}
            >
              <div
                style={{
                  background: msg.role === "user" ? "#1976d2" : "#eee",
                  color: msg.role === "user" ? "white" : "black",
                  padding: "10px 15px",
                  borderRadius: "12px",
                  maxWidth: "70%",
                }}
              >
                <p style={{ margin: 0 }}>{msg.content}</p>
                <small style={{ opacity: 0.6 }}>{formatTime(msg.timestamp)}</small>
              </div>
            </div>
          ))
        )}
        {loading && <p style={{ textAlign: "center", color: "#666" }}>ÿ¨ÿßÿ±Ÿä ÿ™ŸàŸÑŸäÿØ ÿßŸÑÿ±ÿØ...</p>}
      </div>

      <form onSubmit={handleSend} style={{ display: "flex", gap: "10px" }}>
        <input
          type="text"
          placeholder="ÿßŸÉÿ™ÿ® ÿ±ÿ≥ÿßŸÑÿ™ŸÉ..."
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          disabled={loading}
          style={{
            flex: 1,
            padding: "10px",
            borderRadius: "6px",
            border: "1px solid #ccc",
          }}
        />
        <button
          type="submit"
          disabled={loading || !prompt.trim()}
          style={{
            padding: "10px 20px",
            border: "none",
            borderRadius: "6px",
            background: "#1976d2",
            color: "white",
            cursor: "pointer",
          }}
        >
          ÿ•ÿ±ÿ≥ÿßŸÑ
        </button>
      </form>
    </div>
  );
};

export default PublicCompanyChat;
