import React, { useState, useEffect } from 'react';
import { chatService, projectService } from '../services/api';

const ChatInterface = () => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [company, setCompany] = useState(null);
  const [loading, setLoading] = useState(false);
  const [loadingCompany, setLoadingCompany] = useState(true);

  useEffect(() => {
    fetchCompany();
  }, []);

  const fetchCompany = async () => {
    try {
      const data = await projectService.getCompany();
      setCompany(data);
      if (data?._id) fetchChatHistory(data._id);
    } catch (error) {
      console.error('Error fetching company data:', error);
    } finally {
      setLoadingCompany(false);
    }
  };

  const fetchChatHistory = async (companyId) => {
    try {
      const data = await chatService.getChatHistory(companyId);
      setMessages(data || []);
    } catch (error) {
      console.error('Error fetching chat history:', error);
    }
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!message.trim() || !company?._id) return;

    const userMessage = {
      role: 'user',
      content: message,
      timestamp: new Date().toISOString(),
    };

    setMessages(prev => [...prev, userMessage]);
    setMessage('');
    setLoading(true);

    try {
      const response = await chatService.sendMessage(message, company._id);
      const aiMessage = {
        role: 'assistant',
        content: response.message,
        timestamp: new Date().toISOString(),
      };
      setMessages(prev => [...prev, aiMessage]);
    } catch (error) {
      const errorMessage = {
        role: 'assistant',
        content: 'عذراً، حدث خطأ أثناء معالجة الرسالة. حاول مرة أخرى.',
        timestamp: new Date().toISOString(),
        error: true,
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setLoading(false);
    }
  };

  const formatTime = (timestamp) => {
    return new Date(timestamp).toLocaleTimeString('ar-SA', {
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  if (loadingCompany) {
    return (
      <div className="loading">
        <div className="spinner"></div>
      </div>
    );
  }

  if (!company) {
    return (
      <div className="card text-center">
        <h2 style={{ color: '#dc3545', marginBottom: '20px' }}>لم يتم العثور على بيانات الشركة</h2>
        <p style={{ color: '#6c757d' }}>
          تأكد من أن حساب الشركة مسجل بشكل صحيح.
        </p>
      </div>
    );
  }

  return (
    <div className="card">
      <div style={{ marginBottom: '20px' }}>
        <h2 style={{ margin: '0 0 15px 0', color: '#495057' }}>
          الدردشة مع الذكاء الاصطناعي - {company.name}
        </h2>
      </div>

      {/* منطقة الرسائل */}
      <div
        style={{
          height: '400px',
          border: '1px solid #dee2e6',
          borderRadius: '8px',
          padding: '15px',
          marginBottom: '20px',
          overflowY: 'auto',
          background: '#f8f9fa',
        }}
      >
        {messages.length === 0 ? (
          <div style={{ textAlign: 'center', color: '#6c757d', padding: '40px' }}>
            <p>ابدأ محادثة جديدة مع الذكاء الاصطناعي الخاص بشركتك</p>
            <p>يمكنك طرح أسئلة حول منتجاتك أو خدماتك</p>
          </div>
        ) : (
          messages.map((msg, index) => (
            <div
              key={index}
              style={{
                marginBottom: '15px',
                display: 'flex',
                flexDirection: msg.role === 'user' ? 'row-reverse' : 'row',
                alignItems: 'flex-start',
                gap: '10px',
              }}
            >
              <div
                style={{
                  background: msg.role === 'user' ? '#667eea' : 'white',
                  color: msg.role === 'user' ? 'white' : '#495057',
                  padding: '10px 15px',
                  borderRadius: '12px',
                  maxWidth: '70%',
                  boxShadow: msg.role === 'user' ? 'none' : '0 2px 8px rgba(0,0,0,0.1)',
                  border: msg.role === 'user' ? 'none' : '1px solid #dee2e6',
                }}
              >
                <p style={{ margin: '0 0 5px 0', lineHeight: '1.5' }}>{msg.content}</p>
                <p
                  style={{
                    margin: '0',
                    fontSize: '0.8rem',
                    opacity: 0.7,
                  }}
                >
                  {formatTime(msg.timestamp)}
                </p>
              </div>
            </div>
          ))
        )}

        {loading && (
          <div
            style={{
              display: 'flex',
              alignItems: 'center',
              gap: '10px',
              color: '#6c757d',
            }}
          >
            <div className="spinner" style={{ width: '20px', height: '20px' }}></div>
            <span>جاري الرد...</span>
          </div>
        )}
      </div>

      {/* نموذج إرسال الرسالة */}
      <form onSubmit={handleSendMessage}>
        <div style={{ display: 'flex', gap: '10px' }}>
          <input
            type="text"
            className="form-input"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="اكتب رسالتك هنا..."
            disabled={loading}
            style={{ flex: 1 }}
          />
          <button
            type="submit"
            className="btn btn-primary"
            disabled={loading || !message.trim()}
            style={{ whiteSpace: 'nowrap' }}
          >
            إرسال
          </button>
        </div>
      </form>

      {/* نصائح للاستخدام */}
      <div
        style={{
          marginTop: '20px',
          padding: '15px',
          background: '#e3f2fd',
          borderRadius: '8px',
          border: '1px solid #bbdefb',
        }}
      >
        <h4 style={{ margin: '0 0 10px 0', color: '#1976d2' }}>نصائح للاستخدام</h4>
        <ul style={{ margin: '0', paddingRight: '20px', color: '#424242' }}>
          <li>اسأل عن منتجاتك أو خدماتك</li>
          <li>اطلب اقتراحات لتحسين أعمالك</li>
          <li>استفسر عن الأسئلة الشائعة</li>
          <li>اطلب مساعدة في كتابة المحتوى التسويقي</li>
        </ul>
      </div>
    </div>
  );
};

export default ChatInterface;
