import React, { useEffect, useState } from "react";
import axios from "axios";
import { motion } from "framer-motion";
import { chatService, companyService } from "../services/api";
import "./CompanyD"
export default function CompanyDashboard() {
  const [company, setCompany] = useState(null);
  const [loading, setLoading] = useState(true);
  const [copySuccess, setCopySuccess] = useState(false);
  const [aiResponse, setAiResponse] = useState(null);
  const [prompt, setPrompt] = useState("اكتب وصف جذاب لشركتي");

  const token = localStorage.getItem("token");
  const API_URL = "http://localhost:5000/api/company";

  useEffect(() => {
    fetchCompany();
  }, []);

  const fetchCompany = async () => {
    try {
      const res = await axios.get(`${API_URL}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setCompany(res.data);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(company.apiKey);
    setCopySuccess(true);
    setTimeout(() => setCopySuccess(false), 2000);
  };

  const handleTestAI = async () => {
    if (!company?.apiKey) return alert("لم يتم العثور على مفتاح API");
    try {
      const reply = await chatService.sendMessage(prompt, company.apiKey);
      setAiResponse(reply);
    } catch (err) {
      console.error("AI error:", err);
      setAiResponse("حدث خطأ أثناء الاتصال بالذكاء الاصطناعي");
    }
  };

  if (loading) return <div className="text-center mt-10">جارٍ التحميل...</div>;
  if (!company) return <div className="text-center mt-10">لم يتم العثور على الشركة</div>;

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <motion.h1
        className="text-3xl font-bold text-center mb-8"
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
      >
        لوحة تحكم الذكاء الاصطناعي
      </motion.h1>

      {/* بيانات الشركة */}
      <motion.div
        className="bg-white dark:bg-gray-800 shadow-lg rounded-2xl p-6 mb-6"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.2 }}
      >
        <h2 className="text-xl font-semibold mb-2">{company.name}</h2>
        <p className="text-gray-600 mb-4">{company.description}</p>
        <div className="mb-2"><strong>المجال:</strong> {company.industry || "غير محدد"}</div>
        <div className="mb-2"><strong>الرؤية:</strong> {company.vision || "غير محددة"}</div>
        <div className="mb-2"><strong>الرسالة:</strong> {company.mission || "غير محددة"}</div>
      </motion.div>

      {/* مفتاح API */}
      <motion.div
        className="bg-blue-50 dark:bg-gray-700 p-4 rounded-2xl flex justify-between items-center"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.3 }}
      >
        <div>
          <h3 className="font-semibold text-blue-600 dark:text-blue-300">AI API Key</h3>
          <p className="text-sm break-all">{company.apiKey}</p>
        </div>
        <button
          onClick={copyToClipboard}
          className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-lg transition"
        >
          نسخ
        </button>
      </motion.div>

      {copySuccess && (
        <motion.div
          className="text-green-600 text-center mt-4"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
        >
          تم نسخ مفتاح الـAI ✅
        </motion.div>
      )}

      {/* تجربة الذكاء الاصطناعي */}
      <motion.div
        className="bg-gray-100 dark:bg-gray-800 mt-8 p-6 rounded-2xl shadow-md"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.4 }}
      >
        <h3 className="text-lg font-semibold mb-3">جرّب الذكاء الاصطناعي</h3>
        <textarea
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          className="w-full p-3 rounded-lg border border-gray-300 dark:bg-gray-900 dark:text-white"
        />
        <button
          onClick={handleTestAI}
          className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-lg mt-3"
        >
          إرسال
        </button>

        {aiResponse && (
          <motion.div
            className="mt-4 p-4 bg-white dark:bg-gray-900 rounded-xl text-gray-800 dark:text-gray-100"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
          >
            {aiResponse}
          </motion.div>
        )}
      </motion.div>

      {/* تعليمات الاستخدام */}
      <motion.div
        className="bg-gray-100 dark:bg-gray-800 mt-8 p-6 rounded-2xl shadow-md"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5 }}
      >
        <h3 className="text-lg font-semibold mb-3">كيفية استخدام الـAPI</h3>
        <pre className="bg-gray-900 text-green-400 p-4 rounded-lg text-sm overflow-x-auto">
{`POST https://yourdomain.com/api/ai
Content-Type: application/json

{
  "apiKey": "${company.apiKey}",
  "prompt": "اكتب وصف جذاب لشركتي"
}`}
        </pre>
      </motion.div>
    </div>
  );
}
