          100% { text-shadow: none; filter: brightness(0.92); opacity: 0.88 }
        }

        /* glow elements fade out then return */
        @keyframes glow-fade {
          0% { opacity: 0.7; transform: scale(1) }
          40% { opacity: 0; transform: scale(0.9) }
          100% { opacity: 0.7; transform: scale(1) }
        }

