import React, { useState } from "react";
import { useAuth } from "../context/AuthContext";
import { companyService } from "../services/api";
import { useNavigate } from "react-router-dom";

const CompanySetup = () => {
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    industry: "",
    size: "",
    vision: "",
    mission: "",
    values: "",
  });

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [apiKey, setApiKey] = useState("");
  const [apiUrl, setApiUrl] = useState("");

  const { user } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    try {
      const valuesArray = formData.values
        .split(",")
        .map((v) => v.trim())
        .filter((v) => v);

      const companyData = { ...formData, values: valuesArray };

      // ‚úÖ ÿßÿ≥ÿ™ÿØÿπÿßÿ° API ŸÑÿ≠ŸÅÿ∏ ÿßŸÑÿ¥ÿ±ŸÉÿ©
      const response = await companyService.updateCompany(companyData);
      const company = response.data || response;

      // ‚úÖ ŸÅŸä ÿ≠ÿßŸÑÿ© ÿßŸÑŸÜÿ¨ÿßÿ≠
      if (company.apiKey) {
        setApiKey(company.apiKey);

        // ‚úÖ ÿ≠ŸÖÿßŸäÿ© ŸÖŸÜ undefined
        const backendUrl =
          (typeof import.meta !== "undefined" &&
            import.meta.env &&
            import.meta.env.VITE_API_URL) ||
          "http://localhost:5000/api";

        setApiUrl(`${backendUrl}/company/use-model`);
      } else {
        setError("ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ API Key ŸÑŸÑÿ¥ÿ±ŸÉÿ©");
      }
    } catch (error) {
      console.error("‚ùå Company setup error:", error);
      setError(error.response?.data?.error || "ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ≠ŸÅÿ∏ ÿßŸÑÿ¥ÿ±ŸÉÿ©");
    } finally {
      setLoading(false);
    }
  };

  // ‚úÖ ÿ≠ÿßŸÑÿ© ÿßŸÑŸÜÿ¨ÿßÿ≠
  if (apiKey && apiUrl) {
    return (
      <div className="max-w-lg mx-auto mt-12 bg-white p-8 rounded-2xl shadow-lg border border-gray-100">
        <h2 className="text-2xl font-bold text-center text-blue-700 mb-6">
          üéâ ÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ¥ÿ±ŸÉÿ© ÿ®ŸÜÿ¨ÿßÿ≠!
        </h2>

        <div className="space-y-4">
          <div className="bg-gray-100 p-3 rounded-md text-sm">
            <strong>API URL:</strong>
            <p className="break-all text-gray-700">{apiUrl}</p>
          </div>

          <div className="bg-gray-100 p-3 rounded-md text-sm">
            <strong>API Key:</strong>
            <p className="break-all text-gray-700">{apiKey}</p>
          </div>

          <p className="text-gray-600 text-sm text-center">
            ÿßÿ≥ÿ™ÿÆÿØŸÖ Ÿáÿ∞ÿß ÿßŸÑÿ±ÿßÿ®ÿ∑ ŸàÿßŸÑŸÄ API Key ŸÅŸä ÿ£Ÿä ÿ™ÿ∑ÿ®ŸäŸÇ ÿÆÿßÿ±ÿ¨Ÿä ŸÑÿ±ÿ®ÿ∑ ÿßŸÑÿ∞ŸÉÿßÿ°
            ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä ÿ®ÿ¥ÿ±ŸÉÿ™ŸÉ.
          </p>

          <div className="text-center mt-6">
            <button
              onClick={() => navigate("/dashboard")}
              className="bg-blue-600 text-white py-2 px-5 rounded-lg hover:bg-blue-700 transition"
            >
              ÿßŸÑÿπŸàÿØÿ© ÿ•ŸÑŸâ ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ
            </button>
          </div>
        </div>
      </div>
    );
  }

  // ‚úÖ ŸÜŸÖŸàÿ∞ÿ¨ ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ¥ÿ±ŸÉÿ©
  return (
    <div className="max-w-lg mx-auto mt-12 bg-white p-8 rounded-2xl shadow-lg border border-gray-100">
      <h2 className="text-2xl font-bold text-center text-blue-700 mb-6">
        ÿ•ÿπÿØÿßÿØ ÿ¥ÿ±ŸÉÿ™ŸÉ ÿßŸÑÿ¨ÿØŸäÿØÿ©
      </h2>

      <form onSubmit={handleSubmit} className="space-y-4">
        {[
          { name: "name", placeholder: "ÿßÿ≥ŸÖ ÿßŸÑÿ¥ÿ±ŸÉÿ©", type: "input" },
          { name: "description", placeholder: "ŸàÿµŸÅ ÿßŸÑÿ¥ÿ±ŸÉÿ©", type: "textarea" },
          { name: "industry", placeholder: "ÿßŸÑŸÖÿ¨ÿßŸÑ ÿßŸÑÿµŸÜÿßÿπŸä", type: "input" },
          { name: "size", placeholder: "ÿ≠ÿ¨ŸÖ ÿßŸÑÿ¥ÿ±ŸÉÿ©", type: "input" },
          { name: "vision", placeholder: "ÿßŸÑÿ±ÿ§Ÿäÿ©", type: "textarea" },
          { name: "mission", placeholder: "ÿßŸÑÿ±ÿ≥ÿßŸÑÿ©", type: "textarea" },
          {
            name: "values",
            placeholder: "ÿßŸÑŸÇŸäŸÖ (ÿßŸÅÿµŸÑ ÿ®ŸäŸÜŸáÿß ÿ®ŸÅŸàÿßÿµŸÑ)",
            type: "input",
          },
        ].map((field) =>
          field.type === "textarea" ? (
            <textarea
              key={field.name}
              name={field.name}
              placeholder={field.placeholder}
              value={formData[field.name]}
              onChange={handleChange}
              className="w-full p-3 border rounded-md focus:ring-2 focus:ring-blue-400 focus:outline-none"
              required={["name", "description"].includes(field.name)}
            />
          ) : (
            <input
              key={field.name}
              name={field.name}
              placeholder={field.placeholder}
              value={formData[field.name]}
              onChange={handleChange}
              className="w-full p-3 border rounded-md focus:ring-2 focus:ring-blue-400 focus:outline-none"
              required={["name", "description"].includes(field.name)}
            />
          )
        )}

        {error && (
          <p className="text-red-600 text-center text-sm font-medium">{error}</p>
        )}

        <button
          type="submit"
          disabled={loading}
          className="w-full bg-blue-600 text-white py-3 rounded-md font-semibold hover:bg-blue-700 transition"
        >
          {loading ? "ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ≠ŸÅÿ∏..." : "üíæ ÿ≠ŸÅÿ∏ ÿßŸÑÿ¥ÿ±ŸÉÿ©"}
        </button>
      </form>
    </div>
  );
};

export default CompanySetup;
