import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: { 'Content-Type': 'application/json' },
});

// إدخال التوكن في كل طلب
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) config.headers.Authorization = `Bearer ${token}`;
    return config;
  },
  (error) => Promise.reject(error)
);

// التعامل مع أخطاء الاستجابة
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// ==========================
// خدمات المصادقة
// ==========================
export const authService = {
  login: async (email, password) => {
    const response = await api.post('/auth/login', { email, password });
    return response.data;
  },
  register: async (name, email, password) => {
    const response = await api.post('/auth/register', { name, email, password });
    return response.data;
  },
};

// ==========================
// خدمات الشركة
// ==========================
export const companyService = {
  getCompany: async () => {
    const response = await api.get('/company');
    return response.data;
  },
  updateCompany: async (companyData) => {
    const response = await api.post('/company', companyData);
    return response.data;
  },
  getRequests: async () => {
    const response = await api.get('/company/requests');
    return response.data;
  },
  deleteRequest: async (index) => {
    const response = await api.delete(`/company/requests/${index}`);
    return response.data;
  },
  submitExternalRequest: async (apiKey, customerName, product, message) => {
    const response = await api.post('/company/external-request', {
      apiKey, customerName, product, message
    });
    return response.data;
  },
  getTempKey: async (companyId) => {
    try {
      const res = await api.get(`/public/temp-key/${companyId}`);
      if (res.data.success) return res.data.tempKey;
      throw new Error(res.data.error || "Failed to get temp key");
    } catch (err) {
      console.error("Temp key fetch error:", err.response?.status, err.response?.data || err.message);
      throw err;
    }
  },
};

// ==========================
// خدمات المشاريع
// ==========================
export const projectService = {
  getProjects: async () => {
    const response = await api.get('/projects');
    return response.data;
  },
  createProject: async (projectData) => {
    const response = await api.post('/projects', projectData);
    return response.data;
  },
  updateProject: async (id, projectData) => {
    const response = await api.put(`/projects/${id}`, projectData);
    return response.data;
  },
  deleteProject: async (id) => {
    const response = await api.delete(`/projects/${id}`);
    return response.data;
  },
};

// ==========================
// خدمات الشركة العامة للشركات المشتركه / AI Chat
// ==========================
export const publicCompanyService = {
  getCompanyByApiKey: async (apiKey) => {
    try {
      const res = await api.get(`/public/company/${apiKey}`);
      if (res.data.success) return res.data.company;
      throw new Error("Company not found");
    } catch (err) {
      console.error("Fetch company by API key error:", err.response?.data || err.message);
      throw err;
    }
  },
  sendAiMessage: async (apiKey, prompt) => {
    try {
      const res = await api.post("/public/chat", { companyApiKey: apiKey, prompt });
      if (res.data.success) return res.data.reply;
      throw new Error(res.data.error || "No reply from AI");
    } catch (err) {
      console.error("Send AI message error:", err.response?.data || err.message);
      throw err;
    }
  },
};

export default api;
