const Dashboard = () => {
  const [company, setCompany] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [activeTab, setActiveTab] = useState('overview');

  const { user, logout } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    fetchCompanyData();
  }, []);

  const fetchCompanyData = async () => {
    try {
      // simulate API delay
      // await new Promise(resolve => setTimeout(resolve, 1000)); 
      const companyData = await companyService.getCompany();
      setCompany(companyData);
    } catch (error) {
      if (error.response?.status === 404) {
        setError('لم يتم العثور على بيانات الشركة. يرجى إكمال إعداد الشركة أولاً.');
      } else {
        setError('حدث خطأ أثناء تحميل بيانات الشركة');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
      </div>
    );
  }

  return (
    <div className="dashboard-wrapper"> {/* Added a wrapper for overall styling */}
      <div className="container" style={{ paddingTop: 50, paddingBottom: 50 }}> {/* Increased padding */}
        {error ? (
          <div className="card text-center error-message"> {/* Added error-message class */}
            <h2 style={{ color: '#dc3545', marginBottom: '20px' }}>خطأ</h2>
            <p style={{ color: 'var(--text-color-light)', marginBottom: '20px' }}>{error}</p>
            <button
              onClick={() => navigate('/company-setup')}
              className="btn btn-primary"
            >
              إعداد الشركة
            </button>
          </div>
        ) : (
          <>
            {/* Navigation Tabs */}
            <div
              style={{
                display: 'flex',
                gap: '10px',
                marginBottom: '30px',
                background: 'var(--card-bg-dark)', // Using card background
                padding: '10px',
                borderRadius: '12px', // More rounded
                boxShadow: '0 4px 20px rgba(0,0,0,0.3)', // Consistent shadow
                backdropFilter: 'blur(5px)',
              }}
            >
              {[
                { key: 'overview', label: 'نظرة عامة' },
                { key: 'company', label: 'بيانات الشركة' },
                { key: 'requests', label: 'طلبات العملاء' },
                { key: 'chat', label: 'الدردشة' },
                { key: 'company-dashboard', label: 'لوحة تحكم الذكاء الاصطناعي' },
                { key: 'api', label: 'API Key' },
              ].map((tab) => (
                <button
                  key={tab.key}
                  onClick={() => setActiveTab(tab.key)}
                  className={`btn ${
                    activeTab === tab.key ? 'btn-primary' : 'btn-secondary'
                  }`}
                  style={{ flex: 1 }}
                >
                  {tab.label}
                </button>
              ))}
            </div>

            {/* Tab Content */}
            {activeTab === 'overview' && (
              <div className="grid grid-3">
                <InfoCard title="اسم الشركة" value={company?.name || 'غير محدد'} />
                <InfoCard title="القطاع" value={company?.industry || 'غير محدد'} />
                <InfoCard
                  title="عدد الطلبات"
                  value={company?.requests?.length || 0}
                />
              </div>
            )}

            {activeTab === 'company' && (
              <CompanyInfo company={company} onUpdate={fetchCompanyData} />
            )}

            {activeTab === 'requests' && (
              <CustomerRequests company={company} onUpdate={fetchCompanyData} />
            )}
           
            {activeTab === 'chat' && <ChatInterface />}

            {activeTab === 'company-dashboard' && <CompanyDashboard />}

            {activeTab === 'api' && <ApiKeyInfo company={company} />}
          </>
        )}
      </div>
    </div>
  );
};

const InfoCard = ({ title, value }) => (
  <div className="card text-center">
    <h3 style={{ marginBottom: '10px' }}>{title}</h3> {/* Color handled by CSS */}
    <p style={{ fontSize: '1.2rem', fontWeight: '600', color: 'var(--text-color-light)' }}>{value}</p>
  </div>
);

// --- Company Info ---
const CompanyInfo = ({ company, onUpdate }) => {
  const [formData, setFormData] = useState({
    name: company?.name || '',
    description: company?.description || '',
    industry: company?.industry || '',
    size: company?.size || '',
    vision: company?.vision || '',
    mission: company?.mission || '',
    values: company?.values || [],
  });
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState('');

  useEffect(() => {
    if (company) {
      setFormData({
        name: company.name || '',
        description: company.description || '',
        industry: company.industry || '',
        size: company.size || '',
        vision: company.vision || '',
        mission: company.mission || '',
        values: company.values || [],
      });
    }
  }, [company]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleValuesChange = (e) => {
    const values = e.target.value
      .split(',')
      .map((v) => v.trim())
      .filter((v) => v);
    setFormData({ ...formData, values });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setSuccess('');
    try {
      await companyService.updateCompany(formData);
      setSuccess('تم تحديث بيانات الشركة بنجاح');
      onUpdate();
    } catch (error) {
      console.error('Error updating company:', error);
      // You might want to show an error message to the user here
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="card">
      <h2 style={{ marginBottom: '20px' }}>بيانات الشركة</h2> {/* Color handled by CSS */}
      <form onSubmit={handleSubmit}>
        <div className="grid grid-2">
          <InputField label="اسم الشركة" name="name" value={formData.name} onChange={handleChange} />
          <InputField label="القطاع" name="industry" value={formData.industry} onChange={handleChange} />
        </div>
        <TextAreaField label="وصف الشركة" name="description" value={formData.description} onChange={handleChange} />
        <SelectField label="حجم الشركة" name="size" value={formData.size} onChange={handleChange} />
        <TextAreaField label="الرؤية" name="vision" value={formData.vision} onChange={handleChange} />
        <TextAreaField label="الرسالة" name="mission" value={formData.mission} onChange={handleChange} />
        <InputField label="القيم (مفصولة بفواصل)" value={formData.values.join(', ')} onChange={handleValuesChange} />
        {success && <div className="success">{success}</div>}
        <button type="submit" className="btn btn-primary" disabled={loading}>
          {loading ? 'جاري الحفظ...' : 'حفظ التغييرات'}
        </button>
      </form>
    </div>
  );
};

const InputField = ({ label, name, value, onChange }) => (
  <div className="form-group">
    <label className="form-label">{label}</label>
    <input type="text" name={name} className="form-input" value={value} onChange={onChange} />
  </div>
);

const TextAreaField = ({ label, name, value, onChange }) => (
  <div className="form-group">
    <label className="form-label">{label}</label>
    <textarea name={name} className="form-input" value={value} onChange={onChange} rows="3" />
  </div>
);

const SelectField = ({ label, name, value, onChange }) => (
  <div className="form-group">
    <label className="form-label">{label}</label>
    <select name={name} className="form-input" value={value} onChange={onChange}>
      <option value="">اختر حجم الشركة</option>
      <option value="startup">ناشئة (1-10 موظف)</option>
      <option value="small">صغيرة (11-50 موظف)</option>
      <option value="medium">متوسطة (51-200 موظف)</option>
      <option value="large">كبيرة (200+ موظف)</option>
    </select>
  </div>
);

// --- Customer Requests ---
const CustomerRequests = ({ company, onUpdate }) => {
  const [requests, setRequests] = useState(company?.requests || []);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (company?.requests) {
      setRequests(company.requests);
    }
  }, [company?.requests]);

  const handleDeleteRequest = async (index) => {
    if (!window.confirm('هل أنت متأكد من حذف هذا الطلب؟')) return;
    setLoading(true);
    try {
      await companyService.deleteRequest(index);
      setRequests(requests.filter((_, i) => i !== index));
      onUpdate();
    } catch (error) {
      console.error('Error deleting request:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="card">
      <h2 style={{ marginBottom: '20px' }}>طلبات العملاء</h2> {/* Color handled by CSS */}
      {requests.length === 0 ? (
        <p style={{ textAlign: 'center', color: 'rgba(255, 255, 255, 0.7)', padding: '40px' }}>
          لا توجد طلبات حالياً
        </p>
      ) : (
        requests.map((req, i) => (
          <div key={i} style={{ background: 'var(--input-bg-dark)', padding: '15px', marginBottom: '10px', borderRadius: '8px', border: '1px solid var(--input-border-dark)', color: 'var(--text-color-light)' }}>
            <h4>{req.customerName}</h4>
            <p><strong>المنتج:</strong> {req.product}</p>
            <p><strong>الرسالة:</strong> {req.message}</p>
            <button onClick={() => handleDeleteRequest(i)} className="btn btn-secondary" disabled={loading}>حذف</button>
          </div>
        ))
      )}
    </div>
  );
};

// --- API Key Info ---
const ApiKeyInfo = ({ company }) => {
  const [copied, setCopied] = useState(false);
  const copyToClipboard = () => {
    navigator.clipboard.writeText(company?.apiKey || '');
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  return (
    <div className="card">
      <h2 style={{ marginBottom: '20px' }}>مفتاح API</h2> {/* Color handled by CSS */}
      <div style={{ marginBottom: '20px' }}>
        <label className="form-label">مفتاح API الخاص بشركتك</label>
        <div style={{ display: 'flex', gap: '10px' }}>
          <input type="text" className="form-input" value={company?.apiKey || ''} readOnly />
          <button onClick={copyToClipboard} className="btn btn-secondary">
            {copied ? 'تم النسخ!' : 'نسخ'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
