import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

export default function Dashboard() {
  const [activeTab, setActiveTab] = useState("overview");

  const tabs = [
    { id: "overview", label: "نظرة عامة" },
    { id: "users", label: "المستخدمين" },
    { id: "ai", label: "ذكاء AiThor" },
    { id: "settings", label: "الإعدادات" },
  ];

  const renderContent = () => {
    switch (activeTab) {
      case "overview":
        return <p>هذه هي نظرة عامة على النظام.</p>;
      case "users":
        return <p>إدارة المستخدمين وعرض التفاصيل.</p>;
      case "ai":
        return <p>هنا تفاعل الذكاء الاصطناعي الخاص بـ AiThor.</p>;
      case "settings":
        return <p>إعدادات النظام والتحكم العام.</p>;
      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-[#0a0a1a] text-white flex flex-col items-center p-8">
      {/* Title */}
      <h1 className="text-4xl font-bold mb-10 bg-gradient-to-r from-blue-400 via-pink-500 to-purple-600 bg-clip-text text-transparent">
        لوحة تحكم AiThor
      </h1>

      {/* Tabs */}
      <div className="flex gap-4 mb-10 flex-wrap justify-center">
        {tabs.map((tab) => (
          <button
            key={tab.id}
            onClick={() => setActiveTab(tab.id)}
            className={`px-6 py-2 rounded-2xl text-sm font-semibold transition-all ${
              activeTab === tab.id
                ? "bg-gradient-to-r from-blue-500 to-pink-500 shadow-lg shadow-pink-400/20"
                : "bg-[#1a1a2e] hover:bg-[#222244]"
            }`}
          >
            {tab.label}
          </button>
        ))}
      </div>

      {/* Content with animation */}
      <div className="w-full max-w-3xl bg-[#0f0f1f] rounded-3xl p-8 shadow-xl shadow-blue-500/10 backdrop-blur-md">
        <AnimatePresence mode="wait">
          <motion.div
            key={activeTab}
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -10 }}
            transition={{ duration: 0.3 }}
          >
            {renderContent()}
          </motion.div>
        </AnimatePresence>
      </div>

      {/* Footer */}
      <footer className="mt-12 text-sm text-gray-400">
        © {new Date().getFullYear()} AiThor Technologies
      </footer>
    </div>
  );
}
