import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { companyService } from '../services/api';
import { useNavigate } from 'react-router-dom';
import ProjectManagement from './ProjectManagement'; // Assuming these components exist
import ChatInterface from './ChatInterface';
import CompanyDashboard from './CompanyDashboard';
import CompanySetup from './CompanySetup';

import './Dashboard.css';
// Make sure you have Font Awesome installed or linked in your index.html/public/index.html
// e.g., <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0-beta3/css/all.min.css">


const Dashboard = () => {
  const [company, setCompany] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [activeTab, setActiveTab] = useState('overview');
  const [isSidebarOpen, setIsSidebarOpen] = useState(false); // New state for sidebar

  const { logout } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    fetchCompanyData();
  }, []);

  const fetchCompanyData = async () => {
    try {
      const companyData = await companyService.getCompany();
      setCompany(companyData);
      // If company setup is not complete, redirect
      if (!companyData || !companyData.name) {
          setError('لم يتم العثور على بيانات الشركة. يرجى إكمال إعداد الشركة أولاً.');
          // Optionally redirect directly here, or let the error message handle it.
      }
    } catch (error) {
      if (error.response?.status === 404) {
        setError('لم يتم العثور على بيانات الشركة. يرجى إكمال إعداد الشركة أولاً.');
      } else {
        console.error("Failed to fetch company data:", error);
        setError('حدث خطأ أثناء تحميل بيانات الشركة');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  // Helper to toggle sidebar state
  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
      </div>
    );
  }

  // Determine wrapper class based on sidebar state for mobile
  const dashboardWrapperClasses = `dashboard-wrapper ${isSidebarOpen ? 'sidebar-open' : ''}`;

  return (
    <div className={dashboardWrapperClasses}>
      {/* Hamburger icon for mobile - d-lg-none hides on large screens via CSS */}
      <button className="menu-toggle d-lg-none" onClick={toggleSidebar}>
        <span></span>
        <span></span>
        <span></span>
      </button>

      {/* Sidebar Navigation */}
      <aside className="dashboard-sidebar">
        <div className="sidebar-header">
          <h3>لوحة التحكم</h3>
          {/* Close button for mobile sidebar - d-lg-none hides on large screens via CSS */}
          <button className="close-sidebar d-lg-none" onClick={toggleSidebar}>&times;</button>
        </div>
        <nav className="sidebar-nav-links">
          {[
            { key: 'overview', label: 'نظرة عامة', icon: 'fas fa-chart-pie' },
            { key: 'company', label: 'بيانات الشركة', icon: 'fas fa-building' },
            { key: 'requests', label: 'طلبات العملاء', icon: 'fas fa-envelope-open-text' },
            { key: 'chat', label: 'الدردشة', icon: 'fas fa-comments' },
            { key: 'company-dashboard', label: 'لوحة تحكم الذكاء الاصطناعي', icon: 'fas fa-robot' },
            { key: 'api', label: 'API Key', icon: 'fas fa-key' },
          ].map((tab) => (
            <button
              key={tab.key}
              onClick={() => {
                setActiveTab(tab.key);
                setIsSidebarOpen(false); // Close sidebar on mobile after selection
              }}
              className={`btn ${
                activeTab === tab.key ? 'btn-sidebar-primary' : 'btn-sidebar-secondary'
              }`}
            >
              <i className={tab.icon}></i> <span>{tab.label}</span>
            </button>
          ))}
          {/* Logout button */}
          <button
            onClick={handleLogout}
            className="btn btn-sidebar-secondary"
            style={{ marginTop: 'auto' }} // Pushes logout button to the bottom
          >
            <i className="fas fa-sign-out-alt"></i> <span>تسجيل الخروج</span>
          </button>
        </nav>
      </aside>

      {/* === Main Content Area === */}
      <main className="dashboard-main-content">
        <div className="dashboard-content container">
          {error ? (
            <div className="card text-center error-message">
              <h2 style={{ color: 'inherit', marginBottom: '20px' }}>خطأ</h2> {/* inherit color from CSS */}
              <p style={{ color: 'inherit', marginBottom: '20px' }}>{error}</p> {/* inherit color from CSS */}
              <button onClick={() => navigate('/company-setup')} className="btn btn-primary">
                إعداد الشركة
              </button>
            </div>
          ) : (
            <>
              {activeTab === 'overview' && (
                <div className="grid grid-3">
                  <InfoCard title="اسم الشركة" value={company?.name || 'غير محدد'} />
                  <InfoCard title="القطاع" value={company?.industry || 'غير محدد'} />
                  <InfoCard title="عدد الطلبات" value={company?.requests?.length || 0} />
                  {/* Additional info cards for overview */}
                  <InfoCard title="الرؤية" value={company?.vision || 'غير محدد'} />
                  <InfoCard title="الرسالة" value={company?.mission || 'غير محدد'} />
                  <InfoCard title="حجم الشركة" value={company?.size || 'غير محدد'} />
                </div>
              )}
              {activeTab === 'company' && (
                <CompanyInfo company={company} onUpdate={fetchCompanyData} />
              )}
              {activeTab === 'requests' && (
                <CustomerRequests company={company} onUpdate={fetchCompanyData} />
              )}
              {activeTab === 'chat' && <ChatInterface />}
              {activeTab === 'company-dashboard' && <CompanyDashboard />}
              {activeTab === 'api' && <ApiKeyInfo company={company} />}
              {/* Optional: Add ProjectManagement or other components if needed */}
              {/* {activeTab === 'projects' && <ProjectManagement />} */}
            </>
          )}
        </div>
      </main>
    </div>
  );
};

/* === المكونات المساعدة كما هي (يمكن تعديل الأنماط الداخلية إذا كانت تتعارض) === */
const InfoCard = ({ title, value }) => (
  <div className="card text-center">
    <h3>{title}</h3>
    <p style={{ fontSize: '1.2rem', fontWeight: '600' }}>{value}</p>
  </div>
);
const CompanyInfo = ({ company, onUpdate }) => {
  const [formData, setFormData] = useState({
    name: company?.name || '',
    description: company?.description || '',
    industry: company?.industry || '',
    size: company?.size || '',
    vision: company?.vision || '',
    mission: company?.mission || '',
    values: company?.values || [],
  });
  const [isSaving, setIsSaving] = useState(false);

  useEffect(() => {
    setFormData({
      name: company?.name || '',
      description: company?.description || '',
      industry: company?.industry || '',
      size: company?.size || '',
      vision: company?.vision || '',
      mission: company?.mission || '',
      values: company?.values || [],
    });
  }, [company]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSaving(true);
    try {
      await companyService.updateCompany(formData);
      onUpdate();
      alert('تم حفظ بيانات الشركة بنجاح!');
    } catch (error) {
      console.error('Error updating company:', error);
      alert('فشل حفظ بيانات الشركة.');
    } finally {
      setIsSaving(false);
    }
  };

  return (
    <div className="card">
      <h2>بيانات الشركة</h2>
      <form onSubmit={handleSubmit}>
        <div className="grid grid-2">
          <div className="form-group">
            <label className="form-label">اسم الشركة</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              className="form-input"
            />
          </div>
          <div className="form-group">
            <label className="form-label">القطاع</label>
            <input
              type="text"
              name="industry"
              value={formData.industry}
              onChange={handleChange}
              className="form-input"
            />
          </div>
        </div>
        <div className="form-group">
          <label className="form-label">الوصف</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            rows="3"
            className="form-input"
          />
        </div>
        {/* Added more fields from formData for completeness */}
        <div className="form-group">
          <label className="form-label">الرؤية</label>
          <textarea
            name="vision"
            value={formData.vision}
            onChange={handleChange}
            rows="2"
            className="form-input"
          />
        </div>
        <div className="form-group">
          <label className="form-label">الرسالة</label>
          <textarea
            name="mission"
            value={formData.mission}
            onChange={handleChange}
            rows="2"
            className="form-input"
          />
        </div>
        <div className="form-group">
          <label className="form-label">حجم الشركة</label>
          <input
            type="text"
            name="size"
            value={formData.size}
            onChange={handleChange}
            className="form-input"
          />
        </div>
        {/* You might need a more complex input for 'values' array */}
        <button type="submit" className="btn btn-primary" disabled={isSaving}>
          {isSaving ? 'جارٍ الحفظ...' : 'حفظ'}
        </button>
      </form>
    </div>
  );
};
const CustomerRequests = ({ company, onUpdate }) => {
  const [requests, setRequests] = useState(company?.requests || []);
  const [loadingDeletion, setLoadingDeletion] = useState(false); // Renamed to avoid conflict

  useEffect(() => {
    if (company?.requests) {
      setRequests(company.requests);
    }
  }, [company?.requests]);

  const handleDeleteRequest = async (index) => {
    if (!window.confirm('هل أنت متأكد من حذف هذا الطلب؟')) return;
    setLoadingDeletion(true);
    try {
      // Assuming deleteRequest takes company ID and request index/ID
      // You might need to adjust companyService.deleteRequest to take the actual request ID if available
      await companyService.deleteRequest(company?._id, index); // Assuming company ID is needed
      setRequests(requests.filter((_, i) => i !== index));
      onUpdate();
    } catch (error) {
      console.error('Error deleting request:', error);
      alert('فشل حذف الطلب.');
    } finally {
      setLoadingDeletion(false);
    }
  };

  return (
    <div className="card">
      <h2 style={{ marginBottom: '20px' }}>طلبات العملاء</h2>
      {requests.length === 0 ? (
        <p style={{ textAlign: 'center', color: 'rgba(255, 255, 255, 0.7)', padding: '40px' }}>
          لا توجد طلبات حالياً
        </p>
      ) : (
        requests.map((req, i) => (
          <div
            key={i}
            style={{
              background: 'rgba(255, 255, 255, 0.05)', // Use rgba for better consistency with blur
              padding: '15px',
              marginBottom: '15px',
              borderRadius: '10px',
              border: '1px solid rgba(255, 255, 255, 0.1)',
              color: 'var(--text-color-light)', // Use CSS variable or direct color
              display: 'flex',
              flexDirection: 'column',
              gap: '8px',
            }}
          >
            <h4>{req.customerName}</h4>
            <p><strong>المنتج:</strong> {req.product}</p>
            <p><strong>الرسالة:</strong> {req.message}</p>
            <button
              onClick={() => handleDeleteRequest(i)}
              className="btn btn-secondary"
              disabled={loadingDeletion}
              style={{ alignSelf: 'flex-end', marginTop: '10px' }} // Align button to right
            >
              {loadingDeletion ? 'جارٍ الحذف...' : 'حذف'}
            </button>
          </div>
        ))
      )}
    </div>
  );
};
const ApiKeyInfo = ({ company }) => {
  const [copied, setCopied] = useState(false);
  const copyToClipboard = () => {
    navigator.clipboard.writeText(company?.apiKey || '');
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  return (
    <div className="card">
      <h2 style={{ marginBottom: '20px' }}>مفتاح API</h2>
      <div style={{ marginBottom: '20px' }}>
        <label className="form-label">مفتاح API الخاص بشركتك</label>
        <div style={{ display: 'flex', gap: '10px' }}>
          <input type="text" className="form-input" value={company?.apiKey || ''} readOnly />
          <button onClick={copyToClipboard} className="btn btn-secondary">
            {copied ? 'تم النسخ!' : 'نسخ'}
          </button>
        </div>
      </div>
    </div>
  );
};
export default Dashboard;
