import React, { useState } from "react";
import { useAuth } from "../context/AuthContext";
import { companyService } from "../services/api";
import { useNavigate } from "react-router-dom";

const CompanySetup = () => {
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    industry: "",
    size: "",
    vision: "",
    mission: "",
    values: "",
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [apiKey, setApiKey] = useState("");
  const [apiUrl, setApiUrl] = useState("");

  const { user } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    try {
      const valuesArray = formData.values
        .split(",")
        .map((v) => v.trim())
        .filter((v) => v);

      const companyData = { ...formData, values: valuesArray };

      const response = await companyService.updateCompany(companyData);
      const company = response.data || response;

      if (company.apiKey) {
        setApiKey(company.apiKey);
        const backendUrl =
          import.meta.env.VITE_API_URL || "http://localhost:5000/api/company";
        setApiUrl(`${backendUrl}/use-model`);
      } else {
        setError("ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ API Key ŸÑŸÑÿ¥ÿ±ŸÉÿ©");
      }
    } catch (error) {
      console.error("‚ùå Company setup error:", error);
      setError(error.response?.data?.error || "ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ≠ŸÅÿ∏ ÿßŸÑÿ¥ÿ±ŸÉÿ©");
    } finally {
      setLoading(false);
    }
  };

  if (apiKey && apiUrl) {
    return (
      <div className="max-w-lg mx-auto mt-16 bg-white p-8 rounded-2xl shadow-lg border border-gray-100">
        <h2 className="text-2xl font-bold text-center mb-4 text-blue-700">
          üéâ ÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ¥ÿ±ŸÉÿ© ÿ®ŸÜÿ¨ÿßÿ≠
        </h2>
        <p className="text-gray-700 mb-4 text-center leading-relaxed">
          ŸäŸÖŸÉŸÜŸÉ ÿßŸÑÿ¢ŸÜ ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ Ÿàÿßÿ¨Ÿáÿ© ÿßŸÑÿ∞ŸÉÿßÿ° ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä ÿßŸÑÿÆÿßÿµÿ© ÿ®ÿ¥ÿ±ŸÉÿ™ŸÉ.
        </p>

        <div className="bg-gray-50 p-4 rounded-lg border border-gray-200 mb-4">
          <strong className="text-blue-700">API URL:</strong>
          <p className="text-gray-600 text-sm break-all">{apiUrl}</p>
        </div>

        <div className="bg-gray-50 p-4 rounded-lg border border-gray-200 mb-4">
          <strong className="text-blue-700">API Key:</strong>
          <p className="text-gray-600 text-sm break-all">{apiKey}</p>
        </div>

        <p className="text-gray-600 text-center text-sm mb-6">
          ÿßÿ≥ÿ™ÿÆÿØŸÖ Ÿáÿ∞ÿß ÿßŸÑÿ±ÿßÿ®ÿ∑ ŸàÿßŸÑŸÄ API Key ŸÅŸä ÿ£Ÿä ÿ™ÿ∑ÿ®ŸäŸÇ ÿÆÿßÿ±ÿ¨Ÿä ŸÑÿ±ÿ®ÿ∑ ÿßŸÑÿ∞ŸÉÿßÿ° ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä ÿ®ÿ¥ÿ±ŸÉÿ™ŸÉ.
        </p>

        <div className="text-center">
          <button
            onClick={() => navigate("/dashboard")}
            className="bg-gradient-to-r from-blue-500 to-pink-500 text-white py-2 px-6 rounded-lg shadow-md hover:opacity-90 transition-all"
          >
            ÿßŸÑÿπŸàÿØÿ© ÿ•ŸÑŸâ ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-lg mx-auto mt-16 bg-white p-8 rounded-2xl shadow-lg border border-gray-100">
      <h2 className="text-2xl font-bold text-center mb-6 text-blue-700">
        üè¢ ÿ•ÿπÿØÿßÿØ ÿ¥ÿ±ŸÉÿ™ŸÉ ÿßŸÑÿ¨ÿØŸäÿØÿ©
      </h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          name="name"
          placeholder="ÿßÿ≥ŸÖ ÿßŸÑÿ¥ÿ±ŸÉÿ©"
          value={formData.name}
          onChange={handleChange}
          className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-400 focus:border-blue-400 transition-all"
          required
        />
        <textarea
          name="description"
          placeholder="ŸàÿµŸÅ ÿßŸÑÿ¥ÿ±ŸÉÿ©"
          value={formData.description}
          onChange={handleChange}
          className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-400 focus:border-blue-400 transition-all"
          required
        />
        <input
          name="industry"
          placeholder="ÿßŸÑŸÖÿ¨ÿßŸÑ ÿßŸÑÿµŸÜÿßÿπŸä"
          value={formData.industry}
          onChange={handleChange}
          className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-400 focus:border-blue-400 transition-all"
        />
        <input
          name="size"
          placeholder="ÿ≠ÿ¨ŸÖ ÿßŸÑÿ¥ÿ±ŸÉÿ© (ÿπÿØÿØ ÿßŸÑŸÖŸàÿ∏ŸÅŸäŸÜ)"
          value={formData.size}
          onChange={handleChange}
          className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-400 focus:border-blue-400 transition-all"
        />
        <textarea
          name="vision"
          placeholder="ÿ±ÿ§Ÿäÿ© ÿßŸÑÿ¥ÿ±ŸÉÿ©"
          value={formData.vision}
          onChange={handleChange}
          className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-400 focus:border-blue-400 transition-all"
        />
        <textarea
          name="mission"
          placeholder="ÿ±ÿ≥ÿßŸÑÿ© ÿßŸÑÿ¥ÿ±ŸÉÿ©"
          value={formData.mission}
          onChange={handleChange}
          className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-400 focus:border-blue-400 transition-all"
        />
        <input
          name="values"
          placeholder="ÿßŸÑŸÇŸäŸÖ (ÿßŸÅÿµŸÑ ÿ®ŸäŸÜŸáÿß ÿ®ŸÅŸàÿßÿµŸÑ)"
          value={formData.values}
          onChange={handleChange}
          className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-400 focus:border-blue-400 transition-all"
        />
        {error && (
          <p className="text-red-600 text-center font-medium bg-red-50 py-2 rounded-lg">
            {error}
          </p>
        )}
        <button
          type="submit"
          disabled={loading}
          className="w-full bg-gradient-to-r from-blue-500 to-pink-500 text-white py-3 rounded-lg shadow-md hover:opacity-90 transition-all font-semibold"
        >
          {loading ? "üöÄ ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ≠ŸÅÿ∏..." : "üíæ ÿ≠ŸÅÿ∏ ÿßŸÑÿ¥ÿ±ŸÉÿ©"}
        </button>
      </form>
    </div>
  );
};

export default CompanySetup;
