import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { publicCompanyService } from "../services/api";

const CompanyChat = () => {
  const { companyId } = useParams();
  const [company, setCompany] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  useEffect(() => {
    fetchCompany();
  }, []);

  const fetchCompany = async () => {
    try {
      const data = await publicCompanyService.getCompanyByApiKey(companyId);
      setCompany(data);
    } catch (err) {
      console.error("Fetch company error:", err);
    }
  };

  const sendMessage = async () => {
    const trimmed = input.trim();
    if (!trimmed || !company?.apiKey) return;

    setMessages((prev) => [...prev, { sender: "user", text: trimmed }]);
    setInput("");

    try {
      const reply = await publicCompanyService.sendAiMessage(company.apiKey, trimmed);
      setMessages((prev) => [...prev, { sender: "bot", text: reply }]);
    } catch (err) {
      console.error(err);
      setMessages((prev) => [...prev, { sender: "bot", text: "Error contacting AI" }]);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") sendMessage();
  };

  if (!company) return <div className="text-center mt-10">Loading company info...</div>;

  return (
    <div style={{ maxWidth: 600, margin: "0 auto" }}>
      <h2 style={{ textAlign: "center", marginBottom: 10 }}>{company.name}</h2>

      <div
        id="chat-container"
        style={{
          border: "1px solid #ccc",
          padding: 10,
          height: 400,
          overflowY: "auto",
          marginBottom: 10,
        }}
      >
        {messages.map((msg, idx) => (
          <div
            key={idx}
            style={{ marginBottom: 5, color: msg.sender === "user" ? "blue" : "green" }}
          >
            {msg.text}
          </div>
        ))}
      </div>

      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        onKeyPress={handleKeyPress}
        placeholder="Type a message"
        style={{ width: "80%", padding: 8 }}
      />
      <button onClick={sendMessage} style={{ padding: "8px 12px" }}>
        Send
      </button>
    </div>
  );
};

export default CompanyChat;
