// src/services/api.js
import axios from "axios";

// Base URL للسيرفر
const BASE_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

// خدمة المصادقة
export const authService = {
  login: (data) => axios.post(`${BASE_URL}/auth/login`, data),
  register: (data) => axios.post(`${BASE_URL}/auth/register`, data),
  logout: () => axios.post(`${BASE_URL}/auth/logout`),
};

// خدمة الشركة
export const companyService = {
  getCompany: () => axios.get(`${BASE_URL}/company`),
  updateCompany: (data) => axios.put(`${BASE_URL}/company`, data),
  deleteRequest: (companyId, requestIndex) =>
    axios.delete(`${BASE_URL}/company/${companyId}/requests/${requestIndex}`),
};

// خدمة الشركات العامة (public) إذا تحتاج
export const publicCompanyService = {
  getCompany: () => axios.get(`${BASE_URL}/public/company`),
  updateCompany: (data) => axios.put(`${BASE_URL}/public/company`, data),
};

// خدمة المشاريع
export const projectService = {
  getProjects: () => axios.get(`${BASE_URL}/projects`),
  createProject: (data) => axios.post(`${BASE_URL}/projects`, data),
};

// خدمة الدردشة
export const chatService = {
  sendMessage: (data) => axios.post(`${BASE_URL}/chat`, data),
};

// Export default (اختياري)
export default {
  authService,
  companyService,
  publicCompanyService,
  projectService,
  chatService,
};
