import React, { useState, useEffect } from "react";
import { companyService } from "../services/api";



const CompanyChat = ({ companyId }) => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const res = await companyService.getMessages(companyId);
        setMessages(res);
      } catch (err) {
        console.error("Error fetching messages:", err);
      }
    };
    fetchMessages();
  }, [companyId]);

  const sendMessage = async () => {
    if (!input.trim()) return;
    const text = input.trim();
    setMessages(prev => [...prev, { user: "me", text }]);
    setInput("");
    try {
      const res = await companyChatService.sendAiMessage(companyId, text);
      setMessages(prev => [...prev, res]);
    } catch (err) {
      console.error(err);
      setMessages(prev => [...prev, { user: "AI", text: "Error contacting AI" }]);
    }
  };

  return (
    <div>
      {messages.map((m, i) => <div key={i}>{m.user}: {m.text}</div>)}
      <input value={input} onChange={e => setInput(e.target.value)} />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
};

export default CompanyChat;
