import React, { useState, useEffect } from "react";
import { companyChatService } from "../services/api"; // صحح الاستيراد

const CompanyChat = ({ companyId }) => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  useEffect(() => {
    if (!companyId) return; // لو companyId مش موجود
    const fetchMessages = async () => {
      try {
        const res = await companyChatService.getMessages(companyId);
        setMessages(res);
      } catch (err) {
        console.error("Error fetching messages:", err);
      }
    };
    fetchMessages();
  }, [companyId]);

  const sendMessage = async () => {
    if (!input.trim() || !companyId) return;
    const text = input.trim();
    setMessages((prev) => [...prev, { user: "me", text }]);
    setInput("");
    try {
      const res = await companyChatService.sendAiMessage(companyId, text);
      setMessages((prev) => [...prev, res]);
    } catch (err) {
      console.error(err);
      setMessages((prev) => [...prev, { user: "AI", text: "Error contacting AI" }]);
    }
  };

  return (
    <div className="chat-container">
      <div className="messages">
        {messages.map((m, i) => (
          <div key={i} className={`message ${m.user === "AI" ? "ai" : "me"}`}>
            <strong>{m.user}:</strong> {m.text}
          </div>
        ))}
      </div>
      <input
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="اكتب رسالتك..."
      />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
};

export default CompanyChat;
