import React, { useEffect, useState } from "react";
import axios from "axios";
import { motion } from "framer-motion";

export default function CompanyDashboard() {
  const [company, setCompany] = useState(null);
  const [loading, setLoading] = useState(true);
  const [copySuccess, setCopySuccess] = useState(false);

  const token = localStorage.getItem("token"); // توكن المستخدم
  const API_URL = import.meta.env.VITE_API_URL || "http://localhost:5000/api/company";

  useEffect(() => {
    fetchCompany();
  }, []);

  const fetchCompany = async () => {
    try {
      const res = await axios.get(`${API_URL}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setCompany(res.data);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(company.apiKey);
    setCopySuccess(true);
    setTimeout(() => setCopySuccess(false), 2000);
  };

  if (loading) return <div className="text-center mt-10">جارٍ التحميل...</div>;
  if (!company) return <div className="text-center mt-10">لم يتم العثور على الشركة</div>;

  return (
    <div className="p-6 max-w-5xl mx-auto">
      <motion.h1
        className="text-3xl font-bold text-center mb-8"
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
      >
        لوحة تحكم الشركة
      </motion.h1>

      {/* بيانات الشركة */}
      <motion.div
        className="bg-white dark:bg-gray-800 shadow-lg rounded-2xl p-6 mb-6"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.2 }}
      >
        <h2 className="text-xl font-semibold mb-2">{company.name}</h2>
        <p className="text-gray-600 mb-4">{company.description}</p>

        <div className="mb-2">
          <strong>المجال:</strong> {company.industry || "غير محدد"}
        </div>
        <div className="mb-2">
          <strong>الرؤية:</strong> {company.vision || "غير محددة"}
        </div>
        <div className="mb-2">
          <strong>الرسالة:</strong> {company.mission || "غير محددة"}
        </div>
      </motion.div>

      {/* مفتاح الـ API */}
      <motion.div
        className="bg-blue-50 dark:bg-gray-700 p-4 rounded-2xl mb-6 flex justify-between items-center"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.3 }}
      >
        <div>
          <h3 className="font-semibold text-blue-600 dark:text-blue-300">API Key</h3>
          <p className="text-sm break-all">{company.apiKey}</p>
        </div>
        <button
          onClick={copyToClipboard}
          className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-lg transition"
        >
          نسخ
        </button>
      </motion.div>
      {copySuccess && (
        <motion.div
          className="text-green-600 text-center mb-4"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
        >
          تم النسخ بنجاح ✅
        </motion.div>
      )}

      {/* الطلبات */}
      <motion.div
        className="bg-white dark:bg-gray-800 p-6 rounded-2xl shadow-md"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.4 }}
      >
        <h2 className="text-2xl font-semibold mb-4">الطلبات الواردة</h2>
        {company.requests.length === 0 ? (
          <p className="text-gray-500">لا توجد طلبات بعد.</p>
        ) : (
          <div className="space-y-4">
            {company.requests.map((req, i) => (
              <motion.div
                key={i}
                className="border dark:border-gray-700 p-4 rounded-xl hover:bg-gray-50 dark:hover:bg-gray-700 transition"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
              >
                <div className="font-semibold">{req.customerName || "عميل مجهول"}</div>
                <div className="text-sm text-gray-500">{req.product}</div>
                <p className="mt-2">{req.message}</p>
                {req.aiReply && (
                  <p className="mt-2 p-2 bg-blue-50 dark:bg-gray-600 rounded-lg text-sm text-gray-700 dark:text-gray-200">
                    💬 <strong>رد الذكاء الاصطناعي:</strong> {req.aiReply}
                  </p>
                )}
                <div className="text-xs text-gray-400 mt-1">
                  {new Date(req.date).toLocaleString("ar-EG")}
                </div>
              </motion.div>
            ))}
          </div>
        )}
      </motion.div>
    </div>
  );
}
